// Process inventory data
const inventoryData = $json;
const products = [];

// Skip header row and process each product
for (let i = 1; i < inventoryData.length; i++) {
  const row = inventoryData[i];
  if (row.length < 6) continue; // Skip incomplete rows

  const product = {
    id: row[0] || '',
    name: row[1] || '',
    sku: row[2] || '',
    category: row[3] || '',
    currentStock: parseInt(row[4]) || 0,
    minThreshold: parseInt(row[5]) || 10,
    maxThreshold: parseInt(row[6]) || 100,
    unitPrice: parseFloat(row[7]) || 0,
    supplier: row[8] || '',
    lastUpdated: row[9] || new Date().toISOString(),
    status: 'active'
  };

  // Calculate stock status
  if (product.currentStock <= 0) {
    product.stockStatus = 'out_of_stock';
    product.priority = 'critical';
  } else if (product.currentStock <= product.minThreshold) {
    product.stockStatus = 'low_stock';
    product.priority = 'high';
  } else if (product.currentStock >= product.maxThreshold) {
    product.stockStatus = 'overstock';
    product.priority = 'medium';
  } else {
    product.stockStatus = 'in_stock';
    product.priority = 'low';
  }

  // Calculate reorder quantity
  if (product.stockStatus === 'low_stock' || product.stockStatus === 'out_of_stock') {
    product.reorderQuantity = product.maxThreshold - product.currentStock;
    product.reorderValue = product.reorderQuantity * product.unitPrice;
  }

  // Calculate days of stock remaining (assuming 10 units sold per day average)
  const avgDailySales = 10;
  product.daysRemaining = Math.floor(product.currentStock / avgDailySales);

  products.push(product);
}

// Generate summary statistics
const summary = {
  totalProducts: products.length,
  outOfStock: products.filter(p => p.stockStatus === 'out_of_stock').length,
  lowStock: products.filter(p => p.stockStatus === 'low_stock').length,
  overstock: products.filter(p => p.stockStatus === 'overstock').length,
  inStock: products.filter(p => p.stockStatus === 'in_stock').length,
  totalValue: products.reduce((sum, p) => sum + (p.currentStock * p.unitPrice), 0),
  reorderValue: products.reduce((sum, p) => sum + (p.reorderValue || 0), 0),
  criticalItems: products.filter(p => p.priority === 'critical'),
  highPriorityItems: products.filter(p => p.priority === 'high'),
  lastChecked: new Date().toISOString()
};

return {
  json: {
    products,
    summary
  }
};

