// Process stock update
const updateData = $json.body;

// Validate required fields
const requiredFields = ['sku', 'quantity', 'operation'];
const missingFields = requiredFields.filter(field => !updateData[field]);

if (missingFields.length > 0) {
  return {
    json: {
      error: true,
      message: `Missing required fields: ${missingFields.join(', ')}`,
      status: 'validation_failed'
    }
  };
}

// Process the update
const stockUpdate = {
  sku: updateData.sku,
  operation: updateData.operation, // 'add', 'subtract', 'set'
  quantity: parseInt(updateData.quantity) || 0,
  reason: updateData.reason || 'manual_update',
  updatedBy: updateData.updatedBy || 'system',
  timestamp: new Date().toISOString(),
  orderId: updateData.orderId || null,
  notes: updateData.notes || '',
  location: updateData.location || 'main_warehouse'
};

// Validate operation
if (!['add', 'subtract', 'set'].includes(stockUpdate.operation)) {
  return {
    json: {
      error: true,
      message: 'Invalid operation. Must be: add, subtract, or set',
      status: 'invalid_operation'
    }
  };
}

// Add transaction ID
stockUpdate.transactionId = `txn_${Date.now()}_${Math.random().toString(36).substr(2, 6)}`;

return { json: stockUpdate };

// Calculate new stock level
const updateData = $('Stock Update Processor').item(0).json;
const productData = $json;

if (!productData || productData.length === 0) {
  return {
    json: {
      error: true,
      message: `Product with SKU ${updateData.sku} not found`,
      status: 'product_not_found'
    }
  };
}

// Get current stock (column E, index 4)
const currentStock = parseInt(productData[4]) || 0;
let newStock;

switch (updateData.operation) {
  case 'add':
    newStock = currentStock + updateData.quantity;
    break;
  case 'subtract':
    newStock = Math.max(0, currentStock - updateData.quantity);
    break;
  case 'set':
    newStock = updateData.quantity;
    break;
  default:
    return {
      json: {
        error: true,
        message: 'Invalid operation',
        status: 'invalid_operation'
      }
    };
}

// Prepare update result
const updateResult = {
  ...updateData,
  productName: productData[1],
  previousStock: currentStock,
  newStock: newStock,
  stockChange: newStock - currentStock,
  rowNumber: productData.row || 2, // Default to row 2 if not found
  success: true,
  minThreshold: parseInt(productData[5]) || 10,
  maxThreshold: parseInt(productData[6]) || 100
};

// Check for alerts
if (newStock <= 0) {
  updateResult.alertLevel = 'critical';
  updateResult.alertMessage = 'Product is now out of stock!';
} else if (newStock <= updateResult.minThreshold) {
  updateResult.alertLevel = 'warning';
  updateResult.alertMessage = 'Product stock is below minimum threshold!';
} else if (newStock >= updateResult.maxThreshold) {
  updateResult.alertLevel = 'info';
  updateResult.alertMessage = 'Product stock is above maximum threshold!';
} else {
  updateResult.alertLevel = 'none';
}

return { json: updateResult };
