// Parse and validate order data
const data = $input.first().json;

// Validate required fields
const requiredFields = ['orderId', 'customerId', 'items', 'totalAmount', 'customerEmail'];
for (const field of requiredFields) {
  if (!data[field]) {
    throw new Error(`Missing required field: ${field}`);
  }
}

// Validate items array
if (!Array.isArray(data.items) || data.items.length === 0) {
  throw new Error('Order must contain at least one item');
}

// Validate each item
data.items.forEach((item, index) => {
  if (!item.productId || !item.quantity || !item.price) {
    throw new Error(`Item ${index + 1} is missing required fields (productId, quantity, price)`);
  }
  if (item.quantity <= 0 || item.price <= 0) {
    throw new Error(`Item ${index + 1} has invalid quantity or price`);
  }
});

// Calculate totals and validate
const calculatedTotal = data.items.reduce((sum, item) => sum + (item.quantity * item.price), 0);
const totalDifference = Math.abs(calculatedTotal - data.totalAmount);
if (totalDifference > 0.01) {
  throw new Error(`Total amount mismatch. Expected: ${calculatedTotal}, Received: ${data.totalAmount}`);
}

// Structure the order
const order = {
  orderId: data.orderId,
  customerId: data.customerId,
  customerEmail: data.customerEmail,
  customerName: data.customerName || 'Unknown',
  items: data.items.map(item => ({
    productId: item.productId,
    productName: item.productName || `Product ${item.productId}`,
    quantity: parseInt(item.quantity),
    price: parseFloat(item.price),
    subtotal: parseFloat((item.quantity * item.price).toFixed(2))
  })),
  totalAmount: parseFloat(data.totalAmount.toFixed(2)),
  currency: data.currency || 'USD',
  shippingAddress: data.shippingAddress || {},
  billingAddress: data.billingAddress || data.shippingAddress || {},
  paymentMethod: data.paymentMethod || 'unknown',
  paymentStatus: data.paymentStatus || 'pending',
  orderStatus: 'received',
  orderDate: new Date().toISOString(),
  estimatedDelivery: data.estimatedDelivery,
  notes: data.notes || '',
  priority: data.priority || 'normal'
};

// Determine order priority based on amount and customer
if (order.totalAmount > 500) {
  order.priority = 'high';
} else if (order.totalAmount > 100) {
  order.priority = 'medium';
}

// Add VIP customer check
if (data.customerType === 'vip' || order.totalAmount > 1000) {
  order.isVip = true;
  order.priority = 'urgent';
}

return { json: order };

// Check inventory for all items
const order = $input.first().json;
const inventoryChecks = [];
let allInStock = true;
let backorderItems = [];

// Simulate inventory check (in real implementation, this would query your inventory system)
for (const item of order.items) {
  // Mock inventory data
  const mockInventory = {
    [item.productId]: Math.floor(Math.random() * 100) + 10 // Random stock between 10-109
  };

  const availableStock = mockInventory[item.productId] || 0;
  const isInStock = availableStock >= item.quantity;

  if (!isInStock) {
    allInStock = false;
    backorderItems.push({
      ...item,
      availableStock,
      shortfall: item.quantity - availableStock
    });
  }

  inventoryChecks.push({
    productId: item.productId,
    productName: item.productName,
    requestedQuantity: item.quantity,
    availableStock,
    isInStock,
    reserved: isInStock ? item.quantity : availableStock
  });
}

const inventoryResult = {
  ...order,
  inventoryStatus: allInStock ? 'in_stock' : 'partial_backorder',
  inventoryChecks,
  backorderItems,
  canFulfill: allInStock,
  requiresBackorder: !allInStock,
  checkedAt: new Date().toISOString()
};

// Update order status based on inventory
if (allInStock) {
  inventoryResult.orderStatus = 'confirmed';
} else {
  inventoryResult.orderStatus = 'pending_inventory';
}

return { json: inventoryResult };

