// Generate daily order summary
const orders = $input.all();
const today = new Date().toISOString().split('T')[0];

// Filter today's orders
const todayOrders = orders.filter(order => {
  const orderDate = order.json.orderDate ? order.json.orderDate.split('T')[0] : '';
  return orderDate === today;
});

const summary = {
  date: today,
  totalOrders: todayOrders.length,
  totalRevenue: 0,
  averageOrderValue: 0,
  statusBreakdown: {},
  priorityBreakdown: {},
  paymentStatusBreakdown: {},
  inventoryIssues: 0,
  vipOrders: 0,
  topProducts: {},
  currencyBreakdown: {}
};

if (todayOrders.length > 0) {
  // Calculate metrics
  todayOrders.forEach(order => {
    const amount = parseFloat(order.json.totalAmount) || 0;
    summary.totalRevenue += amount;

    // Status breakdown
    const status = order.json.orderStatus || 'unknown';
    summary.statusBreakdown[status] = (summary.statusBreakdown[status] || 0) + 1;

    // Priority breakdown
    const priority = order.json.priority || 'normal';
    summary.priorityBreakdown[priority] = (summary.priorityBreakdown[priority] || 0) + 1;

    // Payment status breakdown
    const paymentStatus = order.json.paymentStatus || 'unknown';
    summary.paymentStatusBreakdown[paymentStatus] = (summary.paymentStatusBreakdown[paymentStatus] || 0) + 1;

    // Currency breakdown
    const currency = order.json.currency || 'USD';
    summary.currencyBreakdown[currency] = (summary.currencyBreakdown[currency] || 0) + amount;

    // Inventory issues
    if (order.json.inventoryStatus === 'partial_backorder') {
      summary.inventoryIssues++;
    }

    // VIP orders
    if (order.json.isVip === 'true' || order.json.isVip === true) {
      summary.vipOrders++;
    }

    // Top products (parse items if they're stored as JSON string)
    try {
      const items = typeof order.json.items === 'string' ? JSON.parse(order.json.items) : order.json.items;
      if (Array.isArray(items)) {
        items.forEach(item => {
          const productName = item.productName || item.productId;
          summary.topProducts[productName] = (summary.topProducts[productName] || 0) + item.quantity;
        });
      }
    } catch (e) {
      // Skip if items can't be parsed
    }
  });

  summary.averageOrderValue = Math.round((summary.totalRevenue / todayOrders.length) * 100) / 100;
}

// Get top 5 products
const topProductsList = Object.entries(summary.topProducts)
  .sort(([,a], [,b]) => b - a)
  .slice(0, 5)
  .map(([product, quantity]) => ({ product, quantity }));

summary.topProductsList = topProductsList;

return { json: summary };

