// Process checkout initiation
const checkoutData = $json.body;

// Validate required fields
const requiredFields = ['customerId', 'items', 'totalAmount', 'currency'];
const missingFields = requiredFields.filter(field => !checkoutData[field]);

if (missingFields.length > 0) {
  return {
    json: {
      error: true,
      message: `Missing required fields: ${missingFields.join(', ')}`,
      status: 'validation_failed'
    }
  };
}

// Generate checkout session
const checkoutSession = {
  sessionId: `checkout_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
  customerId: checkoutData.customerId,
  customerEmail: checkoutData.customerEmail || '',
  customerName: checkoutData.customerName || '',
  items: checkoutData.items,
  subtotal: parseFloat(checkoutData.subtotal) || 0,
  tax: parseFloat(checkoutData.tax) || 0,
  shipping: parseFloat(checkoutData.shipping) || 0,
  discount: parseFloat(checkoutData.discount) || 0,
  totalAmount: parseFloat(checkoutData.totalAmount),
  currency: checkoutData.currency.toUpperCase(),
  paymentMethods: checkoutData.paymentMethods || ['card', 'paypal', 'apple_pay'],
  shippingAddress: checkoutData.shippingAddress || {},
  billingAddress: checkoutData.billingAddress || {},
  metadata: checkoutData.metadata || {},
  createdAt: new Date().toISOString(),
  expiresAt: new Date(Date.now() + 30 * 60 * 1000).toISOString(), // 30 minutes
  status: 'initiated',
  botAssisted: true,
  conversationId: `conv_${Date.now()}`
};

// Calculate processing fee
checkoutSession.processingFee = Math.round(checkoutSession.totalAmount * 0.029 * 100) / 100; // 2.9%
checkoutSession.finalAmount = checkoutSession.totalAmount + checkoutSession.processingFee;

return { json: checkoutSession };

// Process bot response and create checkout UI
const botMessage = $json.choices[0].message.content;
const checkoutData = $('Checkout Processor').item(0).json;

// Create checkout UI data
const checkoutUI = {
  sessionId: checkoutData.sessionId,
  conversationId: checkoutData.conversationId,
  botMessage: botMessage,
  checkoutData: {
    items: checkoutData.items,
    subtotal: checkoutData.subtotal,
    tax: checkoutData.tax,
    shipping: checkoutData.shipping,
    discount: checkoutData.discount,
    processingFee: checkoutData.processingFee,
    totalAmount: checkoutData.finalAmount,
    currency: checkoutData.currency
  },
  paymentOptions: checkoutData.paymentMethods.map(method => ({
    id: method,
    name: method.replace('_', ' ').toUpperCase(),
    icon: `${method}-icon`,
    enabled: true,
    processingTime: method === 'card' ? 'Instant' : method === 'paypal' ? '1-2 minutes' : 'Instant'
  })),
  securityFeatures: [
    'SSL Encrypted',
    'PCI DSS Compliant',
    '3D Secure Authentication',
    'Fraud Protection'
  ],
  progressSteps: [
    { step: 1, name: 'Review Order', status: 'current' },
    { step: 2, name: 'Payment Details', status: 'pending' },
    { step: 3, name: 'Confirmation', status: 'pending' }
  ],
  estimatedCompletion: '2-3 minutes',
  supportOptions: [
    'Live Chat',
    'Phone Support',
    'Email Help'
  ]
};

return { json: checkoutUI };

// Process payment submission
const paymentData = $json.body;

// Validate payment data
const requiredPaymentFields = ['sessionId', 'paymentMethod', 'amount'];
const missingPaymentFields = requiredPaymentFields.filter(field => !paymentData[field]);

if (missingPaymentFields.length > 0) {
  return {
    json: {
      error: true,
      message: `Missing payment fields: ${missingPaymentFields.join(', ')}`,
      status: 'payment_validation_failed'
    }
  };
}

// Simulate payment processing
const paymentResult = {
  sessionId: paymentData.sessionId,
  transactionId: `txn_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
  paymentMethod: paymentData.paymentMethod,
  amount: parseFloat(paymentData.amount),
  currency: paymentData.currency || 'USD',
  status: 'processing',
  processedAt: new Date().toISOString(),
  gatewayResponse: {
    authCode: Math.random().toString(36).substr(2, 8).toUpperCase(),
    referenceNumber: Math.random().toString(36).substr(2, 12).toUpperCase(),
    processingTime: Math.floor(Math.random() * 3000) + 1000 // 1-4 seconds
  },
  riskAssessment: {
    score: Math.floor(Math.random() * 30) + 70, // 70-100 (low risk)
    level: 'low',
    factors: ['verified_customer', 'normal_transaction_pattern', 'secure_payment_method']
  },
  customerData: {
    id: paymentData.customerId,
    email: paymentData.customerEmail,
    ipAddress: paymentData.ipAddress || '127.0.0.1',
    userAgent: paymentData.userAgent || 'Unknown'
  }
};

// Simulate success/failure (95% success rate)
const isSuccess = Math.random() > 0.05;

if (isSuccess) {
  paymentResult.status = 'completed';
  paymentResult.completedAt = new Date().toISOString();
  paymentResult.receiptNumber = `RCP_${Date.now()}`;
} else {
  paymentResult.status = 'failed';
  paymentResult.failureReason = 'insufficient_funds';
  paymentResult.errorCode = 'CARD_DECLINED';
}

return { json: paymentResult };

parameters.prompt:
Generate a success message for a completed payment:

Transaction ID: {{ $json.transactionId }}
Amount: {{ $json.currency }} {{ $json.amount }}
Payment Method: {{ $json.paymentMethod }}
Receipt Number: {{ $json.receiptNumber }}

Create a congratulatory message that:
1. Confirms successful payment
2. Provides transaction details
3. Mentions next steps
4. Offers support if needed
5. Thanks the customer

Keep it professional and reassuring.

Generate a helpful message for a failed payment:

Transaction ID: {{ $json.transactionId }}
Failure Reason: {{ $json.failureReason }}
Error Code: {{ $json.errorCode }}
Payment Method: {{ $json.paymentMethod }}

Create a supportive message that:
1. Acknowledges the payment issue
2. Explains what happened (user-friendly)
3. Suggests solutions
4. Offers alternative payment methods
5. Provides support options

Be empathetic and solution-focused.

parameters.jsCode:
// Combine payment result with bot message
const paymentData = $('Payment Processor').item(0).json;
const botMessage = $json.choices[0].message.content;

const response = {
  success: paymentData.status === 'completed',
  transactionId: paymentData.transactionId,
  status: paymentData.status,
  botMessage: botMessage,
  paymentData: paymentData,
  timestamp: new Date().toISOString(),
  nextSteps: paymentData.status === 'completed' ? [
    'Order confirmation email sent',
    'Processing will begin shortly',
    'Tracking information will be provided',
    'Customer support available 24/7'
  ] : [
    'Please try a different payment method',
    'Contact your bank if issue persists',
    'Our support team is here to help',
    'Your order is saved for 24 hours'
  ]
};

return { json: response };
