// Define portfolio cryptocurrencies
const portfolio = [
  { symbol: 'BTC', name: 'Bitcoin', holdings: 0.5 },
  { symbol: 'ETH', name: 'Ethereum', holdings: 2.0 },
  { symbol: 'ADA', name: 'Cardano', holdings: 1000 },
  { symbol: 'DOT', name: 'Polkadot', holdings: 50 },
  { symbol: 'LINK', name: 'Chainlink', holdings: 100 },
  { symbol: 'SOL', name: 'Solana', holdings: 10 },
  { symbol: 'MATIC', name: 'Polygon', holdings: 500 },
  { symbol: 'AVAX', name: 'Avalanche', holdings: 25 }
];

// Create API requests for each cryptocurrency
const apiRequests = portfolio.map(crypto => ({
  symbol: crypto.symbol,
  name: crypto.name,
  holdings: crypto.holdings,
  apiUrl: `https://api.coingecko.com/api/v3/simple/price?ids=${crypto.name.toLowerCase()}&vs_currencies=usd&include_24hr_change=true&include_market_cap=true&include_24hr_vol=true`,
  timestamp: new Date().toISOString()
}));

return apiRequests.map(request => ({ json: request }));

// Process cryptocurrency price data
const portfolioItem = $('Portfolio Setup').item($itemIndex).json;
const priceData = $json;

// Extract price information
const cryptoKey = Object.keys(priceData)[0];
const cryptoData = priceData[cryptoKey];

if (!cryptoData) {
  return {
    json: {
      error: true,
      message: `No price data found for ${portfolioItem.symbol}`,
      symbol: portfolioItem.symbol
    }
  };
}

// Calculate portfolio metrics
const currentPrice = cryptoData.usd || 0;
const change24h = cryptoData.usd_24h_change || 0;
const marketCap = cryptoData.usd_market_cap || 0;
const volume24h = cryptoData.usd_24h_vol || 0;

const portfolioValue = currentPrice * portfolioItem.holdings;
const change24hValue = (portfolioValue * change24h) / 100;

// Determine trend and signals
let trend = 'neutral';
let signal = 'hold';

if (change24h > 5) {
  trend = 'strong_bullish';
  signal = 'strong_buy';
} else if (change24h > 2) {
  trend = 'bullish';
  signal = 'buy';
} else if (change24h < -5) {
  trend = 'strong_bearish';
  signal = 'strong_sell';
} else if (change24h < -2) {
  trend = 'bearish';
  signal = 'sell';
}

// Risk assessment
let riskLevel = 'medium';
if (Math.abs(change24h) > 10) {
  riskLevel = 'high';
} else if (Math.abs(change24h) < 2) {
  riskLevel = 'low';
}

// Performance classification
let performance = 'neutral';
if (change24h > 0) {
  performance = 'positive';
} else if (change24h < 0) {
  performance = 'negative';
}

const result = {
  symbol: portfolioItem.symbol,
  name: portfolioItem.name,
  holdings: portfolioItem.holdings,
  currentPrice: currentPrice,
  portfolioValue: portfolioValue,
  change24h: change24h,
  change24hValue: change24hValue,
  marketCap: marketCap,
  volume24h: volume24h,
  trend: trend,
  signal: signal,
  riskLevel: riskLevel,
  performance: performance,
  timestamp: new Date().toISOString(),
  priceFormatted: `$${currentPrice.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 6 })}`,
  valueFormatted: `$${portfolioValue.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`,
  changeFormatted: `${change24h > 0 ? '+' : ''}${change24h.toFixed(2)}%`,
  marketCapFormatted: `$${(marketCap / 1000000000).toFixed(2)}B`
};

return { json: result };

// Aggregate all portfolio data
const allItems = $input.all();
const validItems = allItems.filter(item => !item.json.error);
const errorItems = allItems.filter(item => item.json.error);

if (validItems.length === 0) {
  return {
    json: {
      error: true,
      message: 'No valid price data received',
      timestamp: new Date().toISOString()
    }
  };
}

// Calculate total portfolio metrics
const totalValue = validItems.reduce((sum, item) => sum + item.json.portfolioValue, 0);
const totalChange24hValue = validItems.reduce((sum, item) => sum + item.json.change24hValue, 0);
const totalChange24hPercent = totalValue > 0 ? (totalChange24hValue / (totalValue - totalChange24hValue)) * 100 : 0;

// Find best and worst performers
const sortedByPerformance = validItems.sort((a, b) => b.json.change24h - a.json.change24h);
const bestPerformer = sortedByPerformance[0]?.json;
const worstPerformer = sortedByPerformance[sortedByPerformance.length - 1]?.json;

// Count signals
const signals = {
  strong_buy: validItems.filter(item => item.json.signal === 'strong_buy').length,
  buy: validItems.filter(item => item.json.signal === 'buy').length,
  hold: validItems.filter(item => item.json.signal === 'hold').length,
  sell: validItems.filter(item => item.json.signal === 'sell').length,
  strong_sell: validItems.filter(item => item.json.signal === 'strong_sell').length
};

// Risk assessment
const highRiskAssets = validItems.filter(item => item.json.riskLevel === 'high').length;
const portfolioRisk = highRiskAssets > validItems.length / 2 ? 'high' : 
                    highRiskAssets > 0 ? 'medium' : 'low';

// Market sentiment
const positiveAssets = validItems.filter(item => item.json.performance === 'positive').length;
const marketSentiment = positiveAssets > validItems.length / 2 ? 'bullish' : 
                      positiveAssets === validItems.length / 2 ? 'neutral' : 'bearish';

const portfolioSummary = {
  totalAssets: validItems.length,
  totalValue: totalValue,
  totalChange24h: totalChange24hPercent,
  totalChange24hValue: totalChange24hValue,
  bestPerformer: bestPerformer,
  worstPerformer: worstPerformer,
  signals: signals,
  portfolioRisk: portfolioRisk,
  marketSentiment: marketSentiment,
  highRiskAssets: highRiskAssets,
  errors: errorItems.length,
  lastUpdated: new Date().toISOString(),
  assets: validItems.map(item => item.json),
  // Formatted values
  totalValueFormatted: `$${totalValue.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`,
  totalChangeFormatted: `${totalChange24hPercent > 0 ? '+' : ''}${totalChange24hPercent.toFixed(2)}%`,
  totalChangeValueFormatted: `${totalChange24hValue > 0 ? '+' : ''}$${Math.abs(totalChange24hValue).toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`
};

return { json: portfolioSummary };

