// Process custom price alert
const alertData = $json.body;

// Validate alert data
const requiredFields = ['symbol', 'alertType', 'targetPrice'];
const missingFields = requiredFields.filter(field => !alertData[field]);

if (missingFields.length > 0) {
  return {
    json: {
      error: true,
      message: `Missing required fields: ${missingFields.join(', ')}`,
      status: 'validation_failed'
    }
  };
}

// Create price alert
const priceAlert = {
  id: `alert_${Date.now()}_${Math.random().toString(36).substr(2, 6)}`,
  symbol: alertData.symbol.toUpperCase(),
  alertType: alertData.alertType, // 'above', 'below', 'change_percent'
  targetPrice: parseFloat(alertData.targetPrice),
  currentPrice: parseFloat(alertData.currentPrice) || 0,
  changePercent: parseFloat(alertData.changePercent) || 0,
  message: alertData.message || '',
  userId: alertData.userId || 'anonymous',
  createdAt: new Date().toISOString(),
  triggered: false,
  priority: alertData.priority || 'medium'
};

// Validate alert type and target
if (!['above', 'below', 'change_percent'].includes(priceAlert.alertType)) {
  return {
    json: {
      error: true,
      message: 'Invalid alert type. Must be: above, below, or change_percent',
      status: 'invalid_alert_type'
    }
  };
}

// Check if alert should be triggered immediately
let shouldTrigger = false;
let triggerReason = '';

if (priceAlert.currentPrice > 0) {
  switch (priceAlert.alertType) {
    case 'above':
      if (priceAlert.currentPrice >= priceAlert.targetPrice) {
        shouldTrigger = true;
        triggerReason = `Price ${priceAlert.currentPrice} is above target ${priceAlert.targetPrice}`;
      }
      break;
    case 'below':
      if (priceAlert.currentPrice <= priceAlert.targetPrice) {
        shouldTrigger = true;
        triggerReason = `Price ${priceAlert.currentPrice} is below target ${priceAlert.targetPrice}`;
      }
      break;
    case 'change_percent':
      if (Math.abs(priceAlert.changePercent) >= Math.abs(priceAlert.targetPrice)) {
        shouldTrigger = true;
        triggerReason = `24h change ${priceAlert.changePercent}% exceeds threshold ${priceAlert.targetPrice}%`;
      }
      break;
  }
}

priceAlert.shouldTrigger = shouldTrigger;
priceAlert.triggerReason = triggerReason;

if (shouldTrigger) {
  priceAlert.triggered = true;
  priceAlert.triggeredAt = new Date().toISOString();
}

return { json: priceAlert };

