// Define backup sources and configurations
const backupConfig = {
  timestamp: new Date().toISOString(),
  backupId: `backup_${Date.now()}`,
  sources: [
    {
      name: 'Google Sheets Database',
      type: 'googlesheets',
      spreadsheetId: '1BxiMVs0XRA5nFMdKvBdBZjgmUUqptlbs74OgvE2upms',
      sheets: ['Users', 'Orders', 'Analytics']
    },
    {
      name: 'Gmail Attachments',
      type: 'gmail',
      query: 'has:attachment',
      maxResults: 100
    },
    {
      name: 'Google Drive Files',
      type: 'googledrive',
      folderId: '1BxiMVs0XRA5nFMdKvBdBZjgmUUqptlbs74OgvE2upms',
      fileTypes: ['pdf', 'docx', 'xlsx', 'csv']
    }
  ],
  destination: {
    type: 'googledrive',
    backupFolderId: '1BxiMVs0XRA5nFMdKvBdBZjgmUUqptlbs74OgvE2upms'
  }
};

return { json: backupConfig };

// Process and prepare backup data
const config = $input.first().json;
const sheetsData = $input.all()[1] ? $input.all()[1].json : [];
const gmailData = $input.all()[2] ? $input.all()[2].json : [];
const driveFiles = $input.all()[3] ? $input.all()[3].json.files || [] : [];

// Create backup manifest
const backupManifest = {
  backupId: config.backupId,
  timestamp: config.timestamp,
  sources: {
    googleSheets: {
      recordCount: Array.isArray(sheetsData) ? sheetsData.length : 0,
      data: sheetsData
    },
    gmailAttachments: {
      emailCount: Array.isArray(gmailData) ? gmailData.length : 0,
      attachmentCount: gmailData.reduce((count, email) => {
        return count + (email.attachments ? email.attachments.length : 0);
      }, 0),
      data: gmailData
    },
    googleDriveFiles: {
      fileCount: driveFiles.length,
      totalSize: driveFiles.reduce((size, file) => size + (parseInt(file.size) || 0), 0),
      files: driveFiles
    }
  },
  status: 'in_progress'
};

// Create CSV content for sheets data
if (sheetsData.length > 0) {
  const headers = Object.keys(sheetsData[0]);
  const csvContent = [
    headers.join(','),
    ...sheetsData.map(row => headers.map(header => `"${(row[header] || '').toString().replace(/"/g, '""')}"`).join(','))
  ].join('\n');

  backupManifest.csvContent = csvContent;
}

return { json: backupManifest };

// Create manifest file content
const manifest = $input.first().json;

// Remove the CSV content from manifest (it's already uploaded)
const cleanManifest = { ...manifest };
delete cleanManifest.csvContent;

// Add completion status
cleanManifest.status = 'completed';
cleanManifest.completedAt = new Date().toISOString();

// Calculate backup summary
const summary = {
  totalFiles: 1 + manifest.sources.googleDriveFiles.fileCount, // +1 for sheets backup
  totalSize: manifest.sources.googleDriveFiles.totalSize,
  sheetsRecords: manifest.sources.googleSheets.recordCount,
  emailsProcessed: manifest.sources.gmailAttachments.emailCount,
  attachmentsFound: manifest.sources.gmailAttachments.attachmentCount
};

cleanManifest.summary = summary;

return {
  json: {
    ...cleanManifest,
    manifestContent: JSON.stringify(cleanManifest, null, 2)
  }
};

