// Generate monthly backup report
const backupHistory = $input.all();

if (!backupHistory || backupHistory.length === 0) {
  return { json: { message: 'No backup history found' } };
}

// Calculate statistics for the past month
const now = new Date();
const lastMonth = new Date(now.getFullYear(), now.getMonth() - 1, 1);
const thisMonth = new Date(now.getFullYear(), now.getMonth(), 1);

const monthlyBackups = backupHistory.filter(backup => {
  const backupDate = new Date(backup.json.timestamp);
  return backupDate >= lastMonth && backupDate < thisMonth;
});

const stats = {
  totalBackups: monthlyBackups.length,
  successfulBackups: monthlyBackups.filter(b => b.json.status === 'completed').length,
  failedBackups: monthlyBackups.filter(b => b.json.status !== 'completed').length,
  totalFilesBackedUp: monthlyBackups.reduce((sum, b) => sum + (parseInt(b.json.totalFiles) || 0), 0),
  totalSizeBackedUp: monthlyBackups.reduce((sum, b) => sum + (parseInt(b.json.totalSize) || 0), 0),
  totalRecordsBackedUp: monthlyBackups.reduce((sum, b) => sum + (parseInt(b.json.sheetsRecords) || 0), 0),
  month: lastMonth.toLocaleDateString('en-US', { month: 'long', year: 'numeric' })
};

// Calculate success rate
stats.successRate = stats.totalBackups > 0 ? Math.round((stats.successfulBackups / stats.totalBackups) * 100) : 0;

// Convert size to MB
stats.totalSizeMB = Math.round(stats.totalSizeBackedUp / 1024 / 1024);

return { json: stats };

