// Extract expense information from email
const email = $json;
const subject = email.subject || '';
const body = email.textPlain || email.textHtml || '';
const sender = email.from?.value?.[0]?.address || '';
const date = new Date(email.date).toISOString();

// Common expense patterns
const expensePatterns = {
  amount: /\$?([0-9,]+\.?[0-9]*)/g,
  total: /total[:\s]*\$?([0-9,]+\.?[0-9]*)/gi,
  subtotal: /subtotal[:\s]*\$?([0-9,]+\.?[0-9]*)/gi,
  tax: /tax[:\s]*\$?([0-9,]+\.?[0-9]*)/gi
};

// Extract amounts
const amounts = [];
const totalMatch = body.match(expensePatterns.total);
const subtotalMatch = body.match(expensePatterns.subtotal);
const taxMatch = body.match(expensePatterns.tax);

let amount = 0;
if (totalMatch) {
  amount = parseFloat(totalMatch[0].replace(/[^0-9.]/g, ''));
} else if (subtotalMatch) {
  amount = parseFloat(subtotalMatch[0].replace(/[^0-9.]/g, ''));
} else {
  // Find largest amount in email
  const allAmounts = body.match(expensePatterns.amount) || [];
  if (allAmounts.length > 0) {
    amount = Math.max(...allAmounts.map(a => parseFloat(a.replace(/[^0-9.]/g, ''))));
  }
}

// Categorize based on sender
let category = 'Other';
const merchant = sender.toLowerCase();

if (merchant.includes('amazon') || merchant.includes('ebay')) {
  category = 'Shopping';
} else if (merchant.includes('uber') || merchant.includes('lyft') || merchant.includes('taxi')) {
  category = 'Transportation';
} else if (merchant.includes('restaurant') || merchant.includes('food') || merchant.includes('cafe')) {
  category = 'Food & Dining';
} else if (merchant.includes('gas') || merchant.includes('fuel') || merchant.includes('shell') || merchant.includes('exxon')) {
  category = 'Gas & Fuel';
} else if (merchant.includes('hotel') || merchant.includes('airbnb') || merchant.includes('booking')) {
  category = 'Travel';
} else if (merchant.includes('office') || merchant.includes('supply') || merchant.includes('staples')) {
  category = 'Office Supplies';
} else if (merchant.includes('utility') || merchant.includes('electric') || merchant.includes('water')) {
  category = 'Utilities';
}

// Extract merchant name from sender or subject
let merchantName = sender.split('@')[0].replace(/[^a-zA-Z0-9\s]/g, '');
if (subject.toLowerCase().includes('receipt from')) {
  const receiptMatch = subject.match(/receipt from ([^\-\|]+)/i);
  if (receiptMatch) {
    merchantName = receiptMatch[1].trim();
  }
}

const expense = {
  id: email.id,
  date: date,
  amount: amount,
  merchant: merchantName,
  category: category,
  description: subject,
  email: sender,
  source: 'email',
  status: 'pending_review',
  attachments: email.attachments || [],
  rawData: {
    subject: subject,
    sender: sender,
    body: body.substring(0, 500) // First 500 chars for reference
  }
};

return { json: expense };

