// Merge AI categorization with original data
const originalExpense = $('Extract Expense Data').item.json;
const aiResponse = $json.response;

let aiData = {};
try {
  aiData = JSON.parse(aiResponse);
} catch (e) {
  // If AI response is not valid JSON, extract manually
  const categoryMatch = aiResponse.match(/"category"\s*:\s*"([^"]+)"/);
  const descriptionMatch = aiResponse.match(/"description"\s*:\s*"([^"]+)"/);

  aiData = {
    category: categoryMatch ? categoryMatch[1] : originalExpense.category,
    description: descriptionMatch ? descriptionMatch[1] : originalExpense.description
  };
}

const finalExpense = {
  ...originalExpense,
  category: aiData.category || originalExpense.category,
  description: aiData.description || originalExpense.description,
  aiProcessed: true,
  processedAt: new Date().toISOString()
};

return { json: finalExpense };

