// Parse file upload data
const body = $json.body;
const headers = $json.headers;

// Extract file information
const fileData = {
  fileName: body.fileName || headers['x-filename'] || 'unknown_file',
  fileSize: body.fileSize || headers['content-length'] || 0,
  fileType: body.fileType || headers['content-type'] || 'application/octet-stream',
  uploadedBy: body.uploadedBy || headers['x-user-id'] || 'anonymous',
  uploadedAt: new Date().toISOString(),
  fileContent: body.fileContent || body.data || '',
  metadata: body.metadata || {},
  tags: body.tags || [],
  category: body.category || 'general',
  description: body.description || '',
  isPublic: body.isPublic || false,
  processingOptions: body.processingOptions || {}
};

// Determine file extension
const fileExtension = fileData.fileName.split('.').pop()?.toLowerCase() || '';

// Determine processing type based on file type
let processingType = 'unknown';
if (['csv', 'xlsx', 'xls'].includes(fileExtension)) {
  processingType = 'spreadsheet';
} else if (['json', 'xml'].includes(fileExtension)) {
  processingType = 'data';
} else if (['txt', 'log'].includes(fileExtension)) {
  processingType = 'text';
} else if (['pdf', 'doc', 'docx'].includes(fileExtension)) {
  processingType = 'document';
} else if (['jpg', 'jpeg', 'png', 'gif', 'bmp'].includes(fileExtension)) {
  processingType = 'image';
} else if (['mp4', 'avi', 'mov', 'wmv'].includes(fileExtension)) {
  processingType = 'video';
} else if (['mp3', 'wav', 'flac', 'aac'].includes(fileExtension)) {
  processingType = 'audio';
}

// Validate file size (max 50MB)
const maxSize = 50 * 1024 * 1024; // 50MB
const isValidSize = parseInt(fileData.fileSize) <= maxSize;

// Generate unique file ID
const fileId = `file_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

return {
  json: {
    ...fileData,
    fileId,
    fileExtension,
    processingType,
    isValidSize,
    maxSizeExceeded: !isValidSize,
    fileSizeMB: Math.round(parseInt(fileData.fileSize) / (1024 * 1024) * 100) / 100
  }
};

// Process CSV/Excel data
const fileData = $json;
let csvData = fileData.fileContent;

// If it's base64 encoded, decode it
if (fileData.fileContent.startsWith('data:')) {
  const base64Data = fileData.fileContent.split(',')[1];
  csvData = Buffer.from(base64Data, 'base64').toString('utf-8');
}

// Parse CSV data
const lines = csvData.split('\n').filter(line => line.trim());
if (lines.length === 0) {
  return [{
    json: {
      ...fileData,
      error: 'Empty file or invalid format',
      processedData: []
    }
  }];
}

// Extract headers
const headers = lines[0].split(',').map(h => h.trim().replace(/"/g, ''));

// Process data rows
const processedData = [];
for (let i = 1; i < Math.min(lines.length, 1001); i++) { // Limit to 1000 rows
  const values = lines[i].split(',').map(v => v.trim().replace(/"/g, ''));
  if (values.length === headers.length) {
    const row = {};
    headers.forEach((header, index) => {
      row[header] = values[index];
    });
    processedData.push(row);
  }
}

// Generate statistics
const stats = {
  totalRows: lines.length - 1,
  processedRows: processedData.length,
  columns: headers.length,
  columnNames: headers,
  sampleData: processedData.slice(0, 5) // First 5 rows as sample
};

return [{
  json: {
    ...fileData,
    processedData,
    stats,
    processingStatus: 'completed'
  }
}];

// Process image analysis results
const fileData = $json;
const analysisData = $('Analyze Image').item.json;

const imageAnalysis = {
  description: analysisData.choices[0].message.content,
  analysisTimestamp: new Date().toISOString(),
  confidence: 'high' // GPT-4 Vision typically provides high confidence
};

// Extract key information from analysis
const analysis = imageAnalysis.description.toLowerCase();
const detectedObjects = [];
const detectedColors = [];
const hasText = analysis.includes('text') || analysis.includes('writing') || analysis.includes('words');

// Simple object detection based on common terms
const commonObjects = ['person', 'people', 'car', 'building', 'tree', 'animal', 'food', 'document', 'chart', 'graph'];
commonObjects.forEach(obj => {
  if (analysis.includes(obj)) {
    detectedObjects.push(obj);
  }
});

// Simple color detection
const commonColors = ['red', 'blue', 'green', 'yellow', 'black', 'white', 'orange', 'purple', 'pink', 'brown'];
commonColors.forEach(color => {
  if (analysis.includes(color)) {
    detectedColors.push(color);
  }
});

return {
  json: {
    ...fileData,
    imageAnalysis,
    detectedObjects,
    detectedColors,
    hasText,
    processingStatus: 'completed'
  }
};

// Process other file types (text, document, etc.)
const fileData = $json;
let content = fileData.fileContent;

// If it's base64 encoded, decode it
if (content.startsWith('data:')) {
  const base64Data = content.split(',')[1];
  content = Buffer.from(base64Data, 'base64').toString('utf-8');
}

// Basic text analysis
const wordCount = content.split(/\s+/).filter(word => word.length > 0).length;
const characterCount = content.length;
const lineCount = content.split('\n').length;

// Extract first few lines as preview
const lines = content.split('\n');
const preview = lines.slice(0, 10).join('\n');

// Simple keyword extraction (most common words)
const words = content.toLowerCase().match(/\b\w+\b/g) || [];
const wordFreq = {};
words.forEach(word => {
  if (word.length > 3) { // Only count words longer than 3 characters
    wordFreq[word] = (wordFreq[word] || 0) + 1;
  }
});

// Get top 10 most frequent words
const topWords = Object.entries(wordFreq)
  .sort(([,a], [,b]) => b - a)
  .slice(0, 10)
  .map(([word, count]) => ({ word, count }));

// Detect potential data patterns
const hasEmails = /\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Z|a-z]{2,}\b/.test(content);
const hasPhones = /\b\d{3}[-.]?\d{3}[-.]?\d{4}\b/.test(content);
const hasUrls = /https?://(www\.)?[-a-zA-Z0-9@:%._\+~#=]{1,256}\.[a-zA-Z0-9()]{1,6}\b([-a-zA-Z0-9()@:%_\+.~#?&//=]*)/.test(content);
const hasNumbers = /\b\d+\b/.test(content);

const textAnalysis = {
  wordCount,
  characterCount,
  lineCount,
  preview,
  topWords,
  patterns: {
    hasEmails,
    hasPhones,
    hasUrls,
    hasNumbers
  },
  analysisTimestamp: new Date().toISOString()
};

return {
  json: {
    ...fileData,
    textAnalysis,
    processingStatus: 'completed'
  }
};

