// Process and analyze Twitter data
const tweets = $input.all();
const analytics = {
  platform: 'twitter',
  date: new Date().toISOString().split('T')[0],
  totalTweets: tweets.length,
  totalLikes: 0,
  totalRetweets: 0,
  totalReplies: 0,
  avgEngagement: 0,
  topTweets: [],
  sentimentScore: 0,
  hashtagAnalysis: {},
  mentionAnalysis: {},
  timeAnalysis: {}
};

if (tweets.length > 0) {
  // Calculate metrics
  tweets.forEach(tweet => {
    const metrics = tweet.json.public_metrics || {};
    analytics.totalLikes += metrics.like_count || 0;
    analytics.totalRetweets += metrics.retweet_count || 0;
    analytics.totalReplies += metrics.reply_count || 0;

    // Extract hashtags
    const text = tweet.json.text || '';
    const hashtags = text.match(/#\w+/g) || [];
    hashtags.forEach(tag => {
      analytics.hashtagAnalysis[tag] = (analytics.hashtagAnalysis[tag] || 0) + 1;
    });

    // Extract mentions
    const mentions = text.match(/@\w+/g) || [];
    mentions.forEach(mention => {
      analytics.mentionAnalysis[mention] = (analytics.mentionAnalysis[mention] || 0) + 1;
    });

    // Time analysis
    const hour = new Date(tweet.json.created_at).getHours();
    analytics.timeAnalysis[hour] = (analytics.timeAnalysis[hour] || 0) + 1;
  });

  // Calculate average engagement
  const totalEngagement = analytics.totalLikes + analytics.totalRetweets + analytics.totalReplies;
  analytics.avgEngagement = Math.round(totalEngagement / tweets.length * 100) / 100;

  // Find top tweets by engagement
  analytics.topTweets = tweets
    .map(tweet => ({
      id: tweet.json.id,
      text: tweet.json.text?.substring(0, 100) + '...',
      engagement: (tweet.json.public_metrics?.like_count || 0) + 
                 (tweet.json.public_metrics?.retweet_count || 0) + 
                 (tweet.json.public_metrics?.reply_count || 0),
      likes: tweet.json.public_metrics?.like_count || 0,
      retweets: tweet.json.public_metrics?.retweet_count || 0
    }))
    .sort((a, b) => b.engagement - a.engagement)
    .slice(0, 5);

  // Simple sentiment analysis (basic keyword approach)
  const positiveWords = ['good', 'great', 'excellent', 'amazing', 'love', 'awesome', 'fantastic'];
  const negativeWords = ['bad', 'terrible', 'awful', 'hate', 'horrible', 'worst', 'disappointing'];

  let sentimentSum = 0;
  tweets.forEach(tweet => {
    const text = (tweet.json.text || '').toLowerCase();
    const positiveCount = positiveWords.filter(word => text.includes(word)).length;
    const negativeCount = negativeWords.filter(word => text.includes(word)).length;
    sentimentSum += positiveCount - negativeCount;
  });

  analytics.sentimentScore = Math.round((sentimentSum / tweets.length) * 100) / 100;
}

return { json: analytics };

// Process and analyze Facebook data
const posts = $input.all();
const analytics = {
  platform: 'facebook',
  date: new Date().toISOString().split('T')[0],
  totalPosts: posts.length,
  totalLikes: 0,
  totalComments: 0,
  totalShares: 0,
  avgEngagement: 0,
  topPosts: [],
  postTypeAnalysis: {},
  timeAnalysis: {}
};

if (posts.length > 0) {
  // Calculate metrics
  posts.forEach(post => {
    analytics.totalLikes += post.json.likes?.summary?.total_count || 0;
    analytics.totalComments += post.json.comments?.summary?.total_count || 0;
    analytics.totalShares += post.json.shares?.count || 0;

    // Post type analysis
    const hasImage = post.json.attachments?.data?.some(att => att.type === 'photo');
    const hasVideo = post.json.attachments?.data?.some(att => att.type === 'video_inline');
    const hasLink = post.json.attachments?.data?.some(att => att.type === 'share');

    let postType = 'text';
    if (hasVideo) postType = 'video';
    else if (hasImage) postType = 'image';
    else if (hasLink) postType = 'link';

    analytics.postTypeAnalysis[postType] = (analytics.postTypeAnalysis[postType] || 0) + 1;

    // Time analysis
    const hour = new Date(post.json.created_time).getHours();
    analytics.timeAnalysis[hour] = (analytics.timeAnalysis[hour] || 0) + 1;
  });

  // Calculate average engagement
  const totalEngagement = analytics.totalLikes + analytics.totalComments + analytics.totalShares;
  analytics.avgEngagement = Math.round(totalEngagement / posts.length * 100) / 100;

  // Find top posts by engagement
  analytics.topPosts = posts
    .map(post => ({
      id: post.json.id,
      message: post.json.message?.substring(0, 100) + '...' || 'No message',
      engagement: (post.json.likes?.summary?.total_count || 0) + 
                 (post.json.comments?.summary?.total_count || 0) + 
                 (post.json.shares?.count || 0),
      likes: post.json.likes?.summary?.total_count || 0,
      comments: post.json.comments?.summary?.total_count || 0,
      shares: post.json.shares?.count || 0
    }))
    .sort((a, b) => b.engagement - a.engagement)
    .slice(0, 5);
}

return { json: analytics };

// Process and analyze Instagram data
const posts = $input.all();
const analytics = {
  platform: 'instagram',
  date: new Date().toISOString().split('T')[0],
  totalPosts: posts.length,
  totalLikes: 0,
  totalComments: 0,
  avgEngagement: 0,
  topPosts: [],
  mediaTypeAnalysis: {},
  timeAnalysis: {}
};

if (posts.length > 0) {
  // Calculate metrics
  posts.forEach(post => {
    analytics.totalLikes += post.json.like_count || 0;
    analytics.totalComments += post.json.comments_count || 0;

    // Media type analysis
    const mediaType = post.json.media_type || 'unknown';
    analytics.mediaTypeAnalysis[mediaType] = (analytics.mediaTypeAnalysis[mediaType] || 0) + 1;

    // Time analysis
    const hour = new Date(post.json.timestamp).getHours();
    analytics.timeAnalysis[hour] = (analytics.timeAnalysis[hour] || 0) + 1;
  });

  // Calculate average engagement
  const totalEngagement = analytics.totalLikes + analytics.totalComments;
  analytics.avgEngagement = Math.round(totalEngagement / posts.length * 100) / 100;

  // Find top posts by engagement
  analytics.topPosts = posts
    .map(post => ({
      id: post.json.id,
      caption: post.json.caption?.substring(0, 100) + '...' || 'No caption',
      engagement: (post.json.like_count || 0) + (post.json.comments_count || 0),
      likes: post.json.like_count || 0,
      comments: post.json.comments_count || 0,
      mediaType: post.json.media_type
    }))
    .sort((a, b) => b.engagement - a.engagement)
    .slice(0, 5);
}

return { json: analytics };

// Combine analytics from all platforms
const twitterData = $input.first().json;
const facebookData = $input.all()[1]?.json || {};
const instagramData = $input.all()[2]?.json || {};

const combinedAnalytics = {
  reportDate: new Date().toISOString().split('T')[0],
  reportTime: new Date().toISOString(),
  summary: {
    totalPosts: (twitterData.totalTweets || 0) + (facebookData.totalPosts || 0) + (instagramData.totalPosts || 0),
    totalEngagement: (twitterData.totalLikes + twitterData.totalRetweets + twitterData.totalReplies || 0) + 
                    (facebookData.totalLikes + facebookData.totalComments + facebookData.totalShares || 0) + 
                    (instagramData.totalLikes + instagramData.totalComments || 0),
    avgEngagementRate: 0
  },
  platforms: {
    twitter: twitterData,
    facebook: facebookData,
    instagram: instagramData
  },
  insights: [],
  recommendations: []
};

// Calculate overall engagement rate
if (combinedAnalytics.summary.totalPosts > 0) {
  combinedAnalytics.summary.avgEngagementRate = Math.round(
    (combinedAnalytics.summary.totalEngagement / combinedAnalytics.summary.totalPosts) * 100
  ) / 100;
}

// Generate insights
const insights = [];

// Platform performance comparison
const platformEngagement = {
  twitter: twitterData.avgEngagement || 0,
  facebook: facebookData.avgEngagement || 0,
  instagram: instagramData.avgEngagement || 0
};

const bestPlatform = Object.keys(platformEngagement).reduce((a, b) => 
  platformEngagement[a] > platformEngagement[b] ? a : b
);

insights.push(`${bestPlatform.charAt(0).toUpperCase() + bestPlatform.slice(1)} has the highest engagement rate (${platformEngagement[bestPlatform]})`);

// Sentiment analysis (Twitter only)
if (twitterData.sentimentScore !== undefined) {
  const sentiment = twitterData.sentimentScore > 0.5 ? 'positive' : 
                   twitterData.sentimentScore < -0.5 ? 'negative' : 'neutral';
  insights.push(`Overall Twitter sentiment is ${sentiment} (score: ${twitterData.sentimentScore})`);
}

// Top hashtags (Twitter)
if (twitterData.hashtagAnalysis && Object.keys(twitterData.hashtagAnalysis).length > 0) {
  const topHashtag = Object.keys(twitterData.hashtagAnalysis).reduce((a, b) => 
    twitterData.hashtagAnalysis[a] > twitterData.hashtagAnalysis[b] ? a : b
  );
  insights.push(`Most used hashtag: ${topHashtag} (${twitterData.hashtagAnalysis[topHashtag]} times)`);
}

combinedAnalytics.insights = insights;

// Generate recommendations
const recommendations = [];

if (combinedAnalytics.summary.avgEngagementRate < 2) {
  recommendations.push('Consider posting more engaging content to improve overall engagement rate');
}

if (twitterData.sentimentScore < 0) {
  recommendations.push('Monitor Twitter mentions more closely and address negative sentiment');
}

if (instagramData.totalPosts < 5) {
  recommendations.push('Increase Instagram posting frequency for better visibility');
}

combinedAnalytics.recommendations = recommendations;

return { json: combinedAnalytics };

