// Generate weekly analytics summary
const data = $input.all();
const today = new Date();
const weekAgo = new Date(today.getTime() - 7 * 24 * 60 * 60 * 1000);

// Filter data for the last 7 days
const weeklyData = data.filter(row => {
  const rowDate = new Date(row.json.reportDate);
  return rowDate >= weekAgo && rowDate <= today;
});

const weeklyAnalytics = {
  weekStartDate: weekAgo.toISOString().split('T')[0],
  weekEndDate: today.toISOString().split('T')[0],
  totalDays: weeklyData.length,
  summary: {
    totalPosts: weeklyData.reduce((sum, day) => sum + (day.json.totalPosts || 0), 0),
    totalEngagement: weeklyData.reduce((sum, day) => sum + (day.json.totalEngagement || 0), 0),
    avgDailyPosts: 0,
    avgDailyEngagement: 0,
    avgEngagementRate: 0
  },
  platformTrends: {
    twitter: {
      totalPosts: weeklyData.reduce((sum, day) => sum + (day.json.twitterPosts || 0), 0),
      avgEngagement: 0
    },
    facebook: {
      totalPosts: weeklyData.reduce((sum, day) => sum + (day.json.facebookPosts || 0), 0),
      avgEngagement: 0
    },
    instagram: {
      totalPosts: weeklyData.reduce((sum, day) => sum + (day.json.instagramPosts || 0), 0),
      avgEngagement: 0
    }
  },
  bestDay: null,
  worstDay: null,
  trends: []
};

if (weeklyData.length > 0) {
  // Calculate averages
  weeklyAnalytics.summary.avgDailyPosts = Math.round(weeklyAnalytics.summary.totalPosts / weeklyData.length * 100) / 100;
  weeklyAnalytics.summary.avgDailyEngagement = Math.round(weeklyAnalytics.summary.totalEngagement / weeklyData.length * 100) / 100;

  if (weeklyAnalytics.summary.totalPosts > 0) {
    weeklyAnalytics.summary.avgEngagementRate = Math.round(weeklyAnalytics.summary.totalEngagement / weeklyAnalytics.summary.totalPosts * 100) / 100;
  }

  // Platform engagement averages
  const twitterEngagements = weeklyData.map(d => d.json.twitterEngagement || 0).filter(e => e > 0);
  const facebookEngagements = weeklyData.map(d => d.json.facebookEngagement || 0).filter(e => e > 0);
  const instagramEngagements = weeklyData.map(d => d.json.instagramEngagement || 0).filter(e => e > 0);

  weeklyAnalytics.platformTrends.twitter.avgEngagement = twitterEngagements.length > 0 ? 
    Math.round(twitterEngagements.reduce((a, b) => a + b, 0) / twitterEngagements.length * 100) / 100 : 0;
  weeklyAnalytics.platformTrends.facebook.avgEngagement = facebookEngagements.length > 0 ? 
    Math.round(facebookEngagements.reduce((a, b) => a + b, 0) / facebookEngagements.length * 100) / 100 : 0;
  weeklyAnalytics.platformTrends.instagram.avgEngagement = instagramEngagements.length > 0 ? 
    Math.round(instagramEngagements.reduce((a, b) => a + b, 0) / instagramEngagements.length * 100) / 100 : 0;

  // Find best and worst performing days
  const sortedByEngagement = weeklyData.sort((a, b) => (b.json.totalEngagement || 0) - (a.json.totalEngagement || 0));
  weeklyAnalytics.bestDay = {
    date: sortedByEngagement[0].json.reportDate,
    engagement: sortedByEngagement[0].json.totalEngagement || 0,
    posts: sortedByEngagement[0].json.totalPosts || 0
  };
  weeklyAnalytics.worstDay = {
    date: sortedByEngagement[sortedByEngagement.length - 1].json.reportDate,
    engagement: sortedByEngagement[sortedByEngagement.length - 1].json.totalEngagement || 0,
    posts: sortedByEngagement[sortedByEngagement.length - 1].json.totalPosts || 0
  };

  // Generate trends
  const trends = [];

  // Engagement trend
  const firstHalf = weeklyData.slice(0, Math.ceil(weeklyData.length / 2));
  const secondHalf = weeklyData.slice(Math.ceil(weeklyData.length / 2));

  const firstHalfAvg = firstHalf.reduce((sum, day) => sum + (day.json.avgEngagementRate || 0), 0) / firstHalf.length;
  const secondHalfAvg = secondHalf.reduce((sum, day) => sum + (day.json.avgEngagementRate || 0), 0) / secondHalf.length;

  if (secondHalfAvg > firstHalfAvg * 1.1) {
    trends.push('Engagement rate is trending upward');
  } else if (secondHalfAvg < firstHalfAvg * 0.9) {
    trends.push('Engagement rate is trending downward');
  } else {
    trends.push('Engagement rate is stable');
  }

  weeklyAnalytics.trends = trends;
}

return { json: weeklyAnalytics };

