const activities = $input.all()[0].json;
const athleteStats = $input.all()[1].json;
const config = $('Set Configuration').item.json;

// Initialize counters
let totalDistance = 0;
let totalTime = 0;
let totalElevation = 0;
let totalActivities = 0;
const activityTypes = {};
const dailyStats = {};
const weeklyActivities = [];

// Process each activity
activities.forEach(activity => {
  const distanceKm = activity.distance / 1000;
  const timeHours = activity.moving_time / 3600;
  const date = new Date(activity.start_date).toLocaleDateString();
  const type = activity.sport_type || activity.type;

  totalDistance += distanceKm;
  totalTime += activity.moving_time;
  totalElevation += activity.total_elevation_gain || 0;
  totalActivities++;

  // Count by activity type
  activityTypes[type] = (activityTypes[type] || 0) + 1;

  // Daily stats
  if (!dailyStats[date]) {
    dailyStats[date] = { distance: 0, time: 0, activities: 0 };
  }
  dailyStats[date].distance += distanceKm;
  dailyStats[date].time += timeHours;
  dailyStats[date].activities++;

  // Store activity details
  weeklyActivities.push({
    name: activity.name,
    type: type,
    distance_km: distanceKm.toFixed(2),
    time_hours: timeHours.toFixed(2),
    elevation_gain: activity.total_elevation_gain || 0,
    average_speed: activity.average_speed ? (activity.average_speed * 3.6).toFixed(1) : 'N/A',
    max_speed: activity.max_speed ? (activity.max_speed * 3.6).toFixed(1) : 'N/A',
    kudos_count: activity.kudos_count || 0,
    start_date: date,
    suffer_score: activity.suffer_score || 0
  });
});

// Calculate goals progress
const distanceGoalProgress = (totalDistance / config.weekly_distance_goal * 100).toFixed(1);
const timeGoalProgress = ((totalTime / 3600) / config.weekly_time_goal_hours * 100).toFixed(1);

// Calculate averages
const avgDistance = totalActivities > 0 ? (totalDistance / totalActivities).toFixed(2) : 0;
const avgTime = totalActivities > 0 ? ((totalTime / 3600) / totalActivities).toFixed(2) : 0;
const avgSpeed = totalTime > 0 ? ((totalDistance / (totalTime / 3600))).toFixed(1) : 0;

// Find best performances
const longestRide = weeklyActivities.reduce((max, act) => 
  parseFloat(act.distance_km) > parseFloat(max.distance_km || 0) ? act : max, {});
const fastestActivity = weeklyActivities.reduce((max, act) => 
  parseFloat(act.average_speed) > parseFloat(max.average_speed || 0) ? act : max, {});

// Format total time
const totalHours = Math.floor(totalTime / 3600);
const totalMinutes = Math.floor((totalTime % 3600) / 60);

// Create comprehensive report
const weekStart = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toLocaleDateString();
const weekEnd = new Date().toLocaleDateString();

const report = `üìä **Weekly Training Report**
` +
  `üìÖ Period: ${weekStart} - ${weekEnd}

` +

  `üéØ **Goal Progress:**
` +
  `‚Ä¢ Distance: ${totalDistance.toFixed(1)}/${config.weekly_distance_goal} km (${distanceGoalProgress}%)
` +
  `‚Ä¢ Time: ${totalHours}h ${totalMinutes}m/${config.weekly_time_goal_hours}h (${timeGoalProgress}%)

` +

  `üìà **Weekly Summary:**
` +
  `‚Ä¢ Total Activities: ${totalActivities}
` +
  `‚Ä¢ Total Distance: ${totalDistance.toFixed(1)} km
` +
  `‚Ä¢ Total Time: ${totalHours}h ${totalMinutes}m
` +
  `‚Ä¢ Total Elevation: ${totalElevation.toFixed(0)} m
` +
  `‚Ä¢ Average Speed: ${avgSpeed} km/h

` +

  `üèÉ **Activity Breakdown:**
` +
  Object.entries(activityTypes).map(([type, count]) => 
    `‚Ä¢ ${type}: ${count} activities`).join('
') + '

' +

  `üèÜ **Best Performances:**
` +
  `‚Ä¢ Longest Activity: ${longestRide.name || 'N/A'} (${longestRide.distance_km || 0} km)
` +
  `‚Ä¢ Fastest Activity: ${fastestActivity.name || 'N/A'} (${fastestActivity.average_speed || 0} km/h)

` +

  `üìä **Daily Breakdown:**
` +
  Object.entries(dailyStats).map(([date, stats]) => 
    `‚Ä¢ ${date}: ${stats.activities} activities, ${stats.distance.toFixed(1)} km, ${stats.time.toFixed(1)}h`
  ).join('
');

// Prepare data for Google Sheets
const sheetsData = weeklyActivities.map(act => [
  act.start_date,
  act.name,
  act.type,
  act.distance_km,
  act.time_hours,
  act.elevation_gain,
  act.average_speed,
  act.max_speed,
  act.kudos_count,
  act.suffer_score
]);

return [{
  json: {
    report: report,
    weekly_summary: {
      total_activities: totalActivities,
      total_distance: totalDistance.toFixed(1),
      total_time_formatted: `${totalHours}h ${totalMinutes}m`,
      total_elevation: totalElevation.toFixed(0),
      average_speed: avgSpeed,
      distance_goal_progress: distanceGoalProgress,
      time_goal_progress: timeGoalProgress,
      activity_types: activityTypes,
      best_performances: {
        longest: longestRide,
        fastest: fastestActivity
      }
    },
    activities: weeklyActivities,
    sheets_data: sheetsData,
    athlete_stats: athleteStats,
    week_period: `${weekStart} - ${weekEnd}`
  }
}];
