// Parse campaign data
const payload = $json.body;

// Extract campaign information
const campaignData = {
  id: payload.id || `campaign_${Date.now()}`,
  name: payload.name || 'Untitled Campaign',
  type: payload.type || 'newsletter', // newsletter, promotional, welcome, follow-up
  subject: payload.subject || '',
  content: payload.content || '',
  segmentId: payload.segmentId || 'all',
  scheduledTime: payload.scheduledTime || new Date().toISOString(),
  timezone: payload.timezone || 'UTC',
  sendImmediately: payload.sendImmediately || false,
  personalizeContent: payload.personalizeContent || true,
  trackOpens: payload.trackOpens !== false,
  trackClicks: payload.trackClicks !== false,
  fromName: payload.fromName || 'Your Company',
  fromEmail: payload.fromEmail || 'noreply@yourcompany.com',
  replyTo: payload.replyTo || payload.fromEmail || 'noreply@yourcompany.com',
  tags: payload.tags || [],
  customFields: payload.customFields || {},
  testMode: payload.testMode || false,
  testEmails: payload.testEmails || [],
  createdAt: new Date().toISOString()
};

// Validate required fields
const isValid = campaignData.name && campaignData.subject && campaignData.content;

// Determine send time
let sendTime;
if (campaignData.sendImmediately) {
  sendTime = new Date();
} else {
  sendTime = new Date(campaignData.scheduledTime);
}

// Calculate if campaign should be sent now
const shouldSendNow = campaignData.sendImmediately || sendTime <= new Date();

return {
  json: {
    ...campaignData,
    isValid,
    sendTime: sendTime.toISOString(),
    shouldSendNow,
    campaignId: campaignData.id
  }
};

// Generate test email list
const campaignData = $json;
const testEmails = campaignData.testEmails || [];

// If no test emails provided, use default
if (testEmails.length === 0) {
  testEmails.push('test@yourcompany.com');
}

// Create subscriber objects for test emails
const testSubscribers = testEmails.map((email, index) => ({
  email: email,
  firstName: `Test`,
  lastName: `User ${index + 1}`,
  fullName: `Test User ${index + 1}`,
  subscriptionDate: new Date().toISOString(),
  status: 'active',
  segment: 'test',
  customFields: {
    company: 'Test Company',
    industry: 'Testing'
  },
  isTest: true
}));

return testSubscribers.map(subscriber => ({
  json: {
    ...campaignData,
    subscriber
  }
}));

// Prepare subscriber data for email sending
const campaignData = $('Parse Campaign Data').item.json;
const subscribers = $input.all();

if (!subscribers || subscribers.length === 0) {
  return [{
    json: {
      error: 'No subscribers found for this campaign',
      campaignId: campaignData.campaignId
    }
  }];
}

// Process each subscriber
return subscribers.map(item => {
  const subscriber = item.json;

  return {
    json: {
      ...campaignData,
      subscriber: {
        email: subscriber.email,
        firstName: subscriber.firstName || '',
        lastName: subscriber.lastName || '',
        fullName: subscriber.fullName || `${subscriber.firstName || ''} ${subscriber.lastName || ''}`.trim() || 'Valued Customer',
        customFields: subscriber.customFields || {},
        segment: subscriber.segment || 'general',
        subscriptionDate: subscriber.subscriptionDate || '',
        isTest: false
      }
    }
  };
});

