// Use original content without AI personalization
const data = $json;

// Simple placeholder replacement
let personalizedContent = data.content;
let personalizedSubject = data.subject;

// Replace common placeholders
const replacements = {
  '[Name]': data.subscriber.fullName,
  '[FirstName]': data.subscriber.firstName || data.subscriber.fullName.split(' ')[0] || 'there',
  '[LastName]': data.subscriber.lastName || '',
  '[Email]': data.subscriber.email,
  '[Company]': data.subscriber.customFields?.company || '',
  '[Segment]': data.subscriber.segment || ''
};

Object.entries(replacements).forEach(([placeholder, value]) => {
  const regex = new RegExp(placeholder.replace(/[.*+?^${}()|[\]\\]/g, '\\$&'), 'gi');
  personalizedContent = personalizedContent.replace(regex, value);
  personalizedSubject = personalizedSubject.replace(regex, value);
});

return {
  json: {
    ...data,
    personalizedContent,
    personalizedSubject
  }
};

// Process AI-personalized content
const data = $json;
const aiResponse = $('Personalize Content').item.json;

return {
  json: {
    ...data,
    personalizedContent: aiResponse.choices[0].message.content,
    personalizedSubject: data.subject // Keep original subject or extract from AI response if needed
  }
};

// Generate email campaign analytics report
const emailLogs = $input.all();

if (!emailLogs || emailLogs.length === 0) {
  return [{
    json: {
      report: 'No email campaigns found in the last 24 hours.',
      analytics: {
        totalEmails: 0,
        campaigns: 0,
        testEmails: 0,
        productionEmails: 0
      }
    }
  }];
}

// Group by campaign
const campaignGroups = {};
emailLogs.forEach(item => {
  const log = item.json;
  const campaignId = log.campaignId;

  if (!campaignGroups[campaignId]) {
    campaignGroups[campaignId] = {
      campaignId,
      campaignName: log.campaignName,
      campaignType: log.campaignType,
      emails: [],
      totalSent: 0,
      testEmails: 0,
      productionEmails: 0,
      segments: new Set(),
      firstSent: log.sentAt,
      lastSent: log.sentAt
    };
  }

  const campaign = campaignGroups[campaignId];
  campaign.emails.push(log);
  campaign.totalSent++;

  if (log.isTest === 'true' || log.isTest === true) {
    campaign.testEmails++;
  } else {
    campaign.productionEmails++;
  }

  campaign.segments.add(log.segment);

  if (new Date(log.sentAt) < new Date(campaign.firstSent)) {
    campaign.firstSent = log.sentAt;
  }
  if (new Date(log.sentAt) > new Date(campaign.lastSent)) {
    campaign.lastSent = log.sentAt;
  }
});

// Calculate overall statistics
const totalEmails = emailLogs.length;
const totalCampaigns = Object.keys(campaignGroups).length;
const totalTestEmails = emailLogs.filter(item => item.json.isTest === 'true' || item.json.isTest === true).length;
const totalProductionEmails = totalEmails - totalTestEmails;

// Generate report
let report = `ðŸ“§ **Email Campaign Analytics Report**\n`;
report += `ðŸ“… **Period:** Last 24 hours\n`;
report += `â° **Generated:** ${new Date().toISOString()}\n\n`;

report += `ðŸ“Š **Overall Statistics:**\n`;
report += `â€¢ Total Emails Sent: ${totalEmails}\n`;
report += `â€¢ Active Campaigns: ${totalCampaigns}\n`;
report += `â€¢ Production Emails: ${totalProductionEmails}\n`;
report += `â€¢ Test Emails: ${totalTestEmails}\n\n`;

if (totalCampaigns > 0) {
  report += `ðŸ“‹ **Campaign Details:**\n`;

  Object.values(campaignGroups).forEach(campaign => {
    const duration = new Date(campaign.lastSent) - new Date(campaign.firstSent);
    const durationMinutes = Math.round(duration / (1000 * 60));

    report += `\nðŸ“¨ **${campaign.campaignName}**\n`;
    report += `  â€¢ Type: ${campaign.campaignType}\n`;
    report += `  â€¢ Total Sent: ${campaign.totalSent}\n`;
    report += `  â€¢ Production: ${campaign.productionEmails}\n`;
    report += `  â€¢ Test: ${campaign.testEmails}\n`;
    report += `  â€¢ Segments: ${Array.from(campaign.segments).join(', ')}\n`;
    report += `  â€¢ Duration: ${durationMinutes} minutes\n`;
    report += `  â€¢ Started: ${new Date(campaign.firstSent).toLocaleString()}\n`;
  });
}

// Segment analysis
const segmentStats = {};
emailLogs.forEach(item => {
  const segment = item.json.segment || 'unknown';
  segmentStats[segment] = (segmentStats[segment] || 0) + 1;
});

if (Object.keys(segmentStats).length > 0) {
  report += `\nðŸŽ¯ **Segment Performance:**\n`;
  Object.entries(segmentStats).forEach(([segment, count]) => {
    const percentage = Math.round((count / totalEmails) * 100);
    report += `  â€¢ ${segment}: ${count} emails (${percentage}%)\n`;
  });
}

report += `\n[ðŸ“Š View Analytics Dashboard](https://docs.google.com/spreadsheets/d/your-email-analytics-sheet-id)`;

const analytics = {
  totalEmails,
  campaigns: totalCampaigns,
  testEmails: totalTestEmails,
  productionEmails: totalProductionEmails,
  campaignDetails: Object.values(campaignGroups),
  segmentStats
};

return [{
  json: {
    report,
    analytics,
    generatedAt: new Date().toISOString()
  }
}];
