// Process enhanced content
const enhancedContent = $json.choices[0].message.content;
const originalData = $('Content Generator').item(0).json;

// Update all platform content with enhanced version
const updatedPlatforms = { ...originalData.platforms };

// Adapt enhanced content for each platform
updatedPlatforms.twitter.text = enhancedContent;
updatedPlatforms.linkedin.text = enhancedContent + '\n\nWhat strategies have worked best for your team? I\'d love to hear your insights in the comments below! üí≠';
updatedPlatforms.facebook.text = enhancedContent + '\n\nDouble-tap if you agree! üëç Share this with someone who could benefit from automation.';
updatedPlatforms.instagram.text = enhancedContent + '\n\n.\n.\n.\n#automation #productivity #business #workflow #nocode #efficiency #growth #success #entrepreneur #digitaltransformation';

// Add engagement metrics prediction
const engagementPrediction = {
  twitter: {
    expectedLikes: Math.floor(Math.random() * 50) + 10,
    expectedRetweets: Math.floor(Math.random() * 20) + 5,
    expectedReplies: Math.floor(Math.random() * 15) + 3
  },
  linkedin: {
    expectedLikes: Math.floor(Math.random() * 100) + 20,
    expectedComments: Math.floor(Math.random() * 25) + 5,
    expectedShares: Math.floor(Math.random() * 10) + 2
  },
  facebook: {
    expectedLikes: Math.floor(Math.random() * 75) + 15,
    expectedComments: Math.floor(Math.random() * 20) + 3,
    expectedShares: Math.floor(Math.random() * 8) + 1
  },
  instagram: {
    expectedLikes: Math.floor(Math.random() * 200) + 50,
    expectedComments: Math.floor(Math.random() * 30) + 8,
    expectedSaves: Math.floor(Math.random() * 15) + 3
  }
};

return {
  json: {
    ...originalData,
    platforms: updatedPlatforms,
    enhanced: true,
    engagementPrediction,
    readyToPost: true
  }
};

// Process engagement data
const engagementData = $json.body;

// Calculate engagement metrics
const metrics = {
  platform: engagementData.platform || 'unknown',
  postId: engagementData.postId || '',
  campaignId: engagementData.campaignId || '',
  timestamp: new Date().toISOString(),
  likes: parseInt(engagementData.likes) || 0,
  comments: parseInt(engagementData.comments) || 0,
  shares: parseInt(engagementData.shares) || 0,
  clicks: parseInt(engagementData.clicks) || 0,
  impressions: parseInt(engagementData.impressions) || 0,
  reach: parseInt(engagementData.reach) || 0
};

// Calculate engagement rate
metrics.engagementRate = metrics.impressions > 0 ? 
  ((metrics.likes + metrics.comments + metrics.shares) / metrics.impressions * 100).toFixed(2) : 0;

// Performance classification
if (metrics.engagementRate > 5) {
  metrics.performance = 'excellent';
} else if (metrics.engagementRate > 3) {
  metrics.performance = 'good';
} else if (metrics.engagementRate > 1) {
  metrics.performance = 'average';
} else {
  metrics.performance = 'poor';
}

// Generate insights
metrics.insights = [];
if (metrics.comments > metrics.likes * 0.1) {
  metrics.insights.push('High comment engagement - content sparked discussion');
}
if (metrics.shares > metrics.likes * 0.05) {
  metrics.insights.push('High share rate - content is highly valuable');
}
if (metrics.clicks > metrics.impressions * 0.02) {
  metrics.insights.push('Strong click-through rate - compelling call-to-action');
}

return { json: metrics };

