// Process customer support request
const supportData = $json.body;

// Validate required fields
const requiredFields = ['message', 'customerId'];
const missingFields = requiredFields.filter(field => !supportData[field]);

if (missingFields.length > 0) {
  return {
    json: {
      error: true,
      message: `Missing required fields: ${missingFields.join(', ')}`,
      status: 'validation_failed'
    }
  };
}

// Create support session
const supportSession = {
  sessionId: `support_${Date.now()}_${Math.random().toString(36).substr(2, 8)}`,
  customerId: supportData.customerId,
  customerName: supportData.customerName || 'Valued Customer',
  customerEmail: supportData.customerEmail || '',
  customerPhone: supportData.customerPhone || '',
  message: supportData.message,
  channel: supportData.channel || 'web',
  priority: 'medium',
  category: 'general',
  language: supportData.language || 'en',
  timestamp: new Date().toISOString(),
  status: 'new',
  metadata: {
    userAgent: supportData.userAgent || '',
    ipAddress: supportData.ipAddress || '',
    referrer: supportData.referrer || '',
    platform: supportData.platform || 'web'
  }
};

// Analyze message for priority and category
const messageText = supportData.message.toLowerCase();

// Priority detection
const urgentKeywords = ['urgent', 'emergency', 'critical', 'asap', 'immediately', 'broken', 'not working', 'error', 'bug'];
const highKeywords = ['important', 'soon', 'problem', 'issue', 'help', 'support'];

if (urgentKeywords.some(keyword => messageText.includes(keyword))) {
  supportSession.priority = 'urgent';
} else if (highKeywords.some(keyword => messageText.includes(keyword))) {
  supportSession.priority = 'high';
}

// Category detection
const categoryKeywords = {
  billing: ['billing', 'payment', 'invoice', 'charge', 'refund', 'subscription', 'price', 'cost'],
  technical: ['technical', 'bug', 'error', 'not working', 'broken', 'crash', 'slow', 'login', 'password'],
  account: ['account', 'profile', 'settings', 'username', 'email', 'phone', 'address'],
  product: ['product', 'feature', 'how to', 'tutorial', 'guide', 'documentation'],
  shipping: ['shipping', 'delivery', 'tracking', 'order', 'package', 'arrived'],
  cancellation: ['cancel', 'cancellation', 'unsubscribe', 'delete', 'remove']
};

for (const [category, keywords] of Object.entries(categoryKeywords)) {
  if (keywords.some(keyword => messageText.includes(keyword))) {
    supportSession.category = category;
    break;
  }
}

// Estimate complexity
const wordCount = supportData.message.split(' ').length;
if (wordCount > 100) {
  supportSession.complexity = 'high';
} else if (wordCount > 50) {
  supportSession.complexity = 'medium';
} else {
  supportSession.complexity = 'low';
}

return { json: supportSession };

