// Process AI response and determine next actions
const aiResponse = $json.choices[0].message.content;
const supportData = $('Support Request Processor').item(0).json;

// Analyze AI response for escalation indicators
const escalationKeywords = ['escalate', 'human agent', 'specialist', 'complex', 'technical team', 'manager'];
const needsEscalation = escalationKeywords.some(keyword => 
  aiResponse.toLowerCase().includes(keyword)
) || supportData.priority === 'urgent';

// Determine response type
let responseType = 'resolved';
if (needsEscalation) {
  responseType = 'escalated';
} else if (aiResponse.toLowerCase().includes('follow up') || aiResponse.toLowerCase().includes('more information')) {
  responseType = 'pending_info';
}

// Calculate satisfaction prediction
const positiveWords = ['help', 'solve', 'solution', 'resolve', 'fix', 'assist'];
const satisfactionScore = positiveWords.filter(word => 
  aiResponse.toLowerCase().includes(word)
).length * 20; // 0-100 scale

// Generate response data
const responseData = {
  sessionId: supportData.sessionId,
  aiResponse: aiResponse,
  responseType: responseType,
  needsEscalation: needsEscalation,
  satisfactionPrediction: Math.min(satisfactionScore + 60, 100), // Base 60% + bonus
  estimatedResolutionTime: responseType === 'resolved' ? '0 minutes' : 
                          responseType === 'pending_info' ? '2-4 hours' : 
                          '4-24 hours',
  suggestedActions: [],
  followUpRequired: responseType !== 'resolved',
  timestamp: new Date().toISOString(),
  supportData: supportData
};

// Add suggested actions based on category
switch (supportData.category) {
  case 'billing':
    responseData.suggestedActions.push('Check payment history', 'Review billing settings');
    break;
  case 'technical':
    responseData.suggestedActions.push('Clear browser cache', 'Try different browser', 'Check system requirements');
    break;
  case 'account':
    responseData.suggestedActions.push('Verify email address', 'Update profile information');
    break;
  case 'product':
    responseData.suggestedActions.push('Check documentation', 'Watch tutorial videos');
    break;
  case 'shipping':
    responseData.suggestedActions.push('Track package', 'Contact shipping carrier');
    break;
}

// Add escalation actions if needed
if (needsEscalation) {
  responseData.suggestedActions.push('Escalate to human agent', 'Create priority ticket');
}

return { json: responseData };

