// Process customer feedback
const feedbackData = $json.body;

// Validate feedback data
const requiredFields = ['sessionId', 'rating'];
const missingFields = requiredFields.filter(field => !feedbackData[field]);

if (missingFields.length > 0) {
  return {
    json: {
      error: true,
      message: `Missing required fields: ${missingFields.join(', ')}`,
      status: 'validation_failed'
    }
  };
}

// Process feedback
const feedback = {
  sessionId: feedbackData.sessionId,
  rating: parseInt(feedbackData.rating), // 1-5 scale
  comment: feedbackData.comment || '',
  resolved: feedbackData.resolved === true || feedbackData.resolved === 'true',
  helpful: feedbackData.helpful === true || feedbackData.helpful === 'true',
  categories: feedbackData.categories || [],
  improvementSuggestions: feedbackData.improvementSuggestions || '',
  customerId: feedbackData.customerId || '',
  timestamp: new Date().toISOString()
};

// Validate rating
if (feedback.rating < 1 || feedback.rating > 5) {
  return {
    json: {
      error: true,
      message: 'Rating must be between 1 and 5',
      status: 'invalid_rating'
    }
  };
}

// Analyze feedback sentiment
const comment = feedback.comment.toLowerCase();
const positiveWords = ['good', 'great', 'excellent', 'helpful', 'satisfied', 'thank', 'appreciate'];
const negativeWords = ['bad', 'terrible', 'awful', 'unhelpful', 'frustrated', 'disappointed', 'angry'];

const positiveCount = positiveWords.filter(word => comment.includes(word)).length;
const negativeCount = negativeWords.filter(word => comment.includes(word)).length;

if (positiveCount > negativeCount) {
  feedback.sentiment = 'positive';
} else if (negativeCount > positiveCount) {
  feedback.sentiment = 'negative';
} else {
  feedback.sentiment = 'neutral';
}

// Determine feedback category
if (feedback.rating >= 4 && feedback.resolved) {
  feedback.category = 'satisfied';
} else if (feedback.rating <= 2 || !feedback.resolved) {
  feedback.category = 'dissatisfied';
} else {
  feedback.category = 'neutral';
}

// Calculate NPS score contribution
if (feedback.rating >= 4) {
  feedback.npsCategory = 'promoter';
} else if (feedback.rating >= 3) {
  feedback.npsCategory = 'passive';
} else {
  feedback.npsCategory = 'detractor';
}

return { json: feedback };

