// Get team members and their timezones
const teamMembers = [
  { name: 'Alice', slackId: 'U123456', timezone: 'America/New_York' },
  { name: 'Bob', slackId: 'U234567', timezone: 'Europe/London' },
  { name: 'Charlie', slackId: 'U345678', timezone: 'Asia/Tokyo' }
];

const today = new Date();
const dayOfWeek = today.toLocaleDateString('en-US', { weekday: 'long' });
const date = today.toLocaleDateString('en-US', { 
  year: 'numeric', 
  month: 'long', 
  day: 'numeric' 
});

// Generate standup questions
const questions = [
  'ðŸŽ¯ What did you accomplish yesterday?',
  'ðŸ“‹ What are you working on today?',
  'ðŸš§ Any blockers or challenges?',
  'ðŸ¤ Do you need help with anything?'
];

const standupMessage = `Good morning team! ðŸŒ…

**${dayOfWeek} Standup - ${date}**

Time for our daily standup! Please share:

${questions.join('\n')}

*Please respond in this thread within the next hour.*

${teamMembers.map(member => `<@${member.slackId}>`).join(' ')}`;

return {
  json: {
    message: standupMessage,
    teamMembers: teamMembers,
    date: date,
    dayOfWeek: dayOfWeek
  }
};

// Check who hasn't responded to standup
const messages = $input.all();
const teamMembers = [
  { name: 'Alice', slackId: 'U123456' },
  { name: 'Bob', slackId: 'U234567' },
  { name: 'Charlie', slackId: 'U345678' }
];

// Find standup message from bot
const standupMessage = messages.find(msg => 
  msg.json.text && msg.json.text.includes('Time for our daily standup')
);

if (!standupMessage) {
  return { json: { noStandupFound: true } };
}

// Get thread replies
const threadReplies = messages.filter(msg => 
  msg.json.thread_ts === standupMessage.json.ts
);

// Check who responded
const respondedUsers = threadReplies.map(msg => msg.json.user);
const missingUsers = teamMembers.filter(member => 
  !respondedUsers.includes(member.slackId)
);

if (missingUsers.length === 0) {
  return { json: { allResponded: true } };
}

const reminderMessage = `ðŸ‘‹ Friendly reminder!

The following team members haven't shared their standup update yet:

${missingUsers.map(user => `â€¢ <@${user.slackId}>`).join('\n')}

Please share your update when you have a moment! ðŸ™`;

return {
  json: {
    needsReminder: true,
    missingUsers: missingUsers,
    reminderMessage: reminderMessage,
    threadTs: standupMessage.json.ts
  }
};

// Generate weekly standup summary
const messages = $input.all();
const teamMembers = [
  { name: 'Alice', slackId: 'U123456' },
  { name: 'Bob', slackId: 'U234567' },
  { name: 'Charlie', slackId: 'U345678' }
];

// Filter standup messages and responses
const standupMessages = messages.filter(msg => 
  msg.json.text && msg.json.text.includes('Time for our daily standup')
);

const responses = messages.filter(msg => 
  msg.json.thread_ts && standupMessages.some(standup => standup.json.ts === msg.json.thread_ts)
);

// Calculate participation
const participationStats = {};
teamMembers.forEach(member => {
  const userResponses = responses.filter(msg => msg.json.user === member.slackId);
  participationStats[member.name] = {
    responses: userResponses.length,
    percentage: Math.round((userResponses.length / standupMessages.length) * 100)
  };
});

// Generate summary
const weekStart = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toLocaleDateString();
const weekEnd = new Date().toLocaleDateString();

const summary = `ðŸ“Š **Weekly Standup Summary** (${weekStart} - ${weekEnd})

` +
  `**Participation Stats:**
` +
  Object.entries(participationStats).map(([name, stats]) => 
    `â€¢ ${name}: ${stats.responses}/${standupMessages.length} days (${stats.percentage}%)`
  ).join('\n') +
  `

**Total Standups:** ${standupMessages.length}
` +
  `**Total Responses:** ${responses.length}

` +
  `Great work this week, team! ðŸŽ‰`;

return {
  json: {
    summary: summary,
    stats: participationStats,
    totalStandups: standupMessages.length,
    totalResponses: responses.length
  }
};
