// Parse webhook payload and determine automation type
const payload = $json.body;
const headers = $json.headers;

// Extract automation request data
const automationData = {
  id: payload.id || `auto_${Date.now()}`,
  type: payload.type || 'unknown', // task, reminder, notification, data-sync, report
  action: payload.action || 'create', // create, update, delete, execute
  source: payload.source || headers['user-agent'] || 'unknown',
  userId: payload.userId || headers['x-user-id'] || 'anonymous',
  priority: payload.priority || 'medium', // low, medium, high, urgent
  scheduledTime: payload.scheduledTime || null,
  data: payload.data || {},
  metadata: payload.metadata || {},
  tags: payload.tags || [],
  category: payload.category || 'general',
  description: payload.description || '',
  recipients: payload.recipients || [],
  settings: payload.settings || {},
  createdAt: new Date().toISOString()
};

// Validate required fields
const isValid = automationData.type !== 'unknown' && automationData.action;

// Determine if this should be executed immediately or scheduled
const shouldExecuteNow = !automationData.scheduledTime || new Date(automationData.scheduledTime) <= new Date();

// Generate execution context
const executionContext = {
  requestId: `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
  timestamp: new Date().toISOString(),
  source: automationData.source,
  userId: automationData.userId
};

return {
  json: {
    ...automationData,
    isValid,
    shouldExecuteNow,
    executionContext
  }
};

// Process task automation
const data = $json;
const taskData = data.data;

// Create task object
const task = {
  id: data.id,
  title: taskData.title || 'Untitled Task',
  description: taskData.description || data.description || '',
  assignee: taskData.assignee || data.recipients[0] || 'unassigned',
  priority: data.priority,
  status: taskData.status || 'pending',
  dueDate: taskData.dueDate || data.scheduledTime || null,
  category: data.category,
  tags: data.tags,
  project: taskData.project || 'General',
  estimatedHours: taskData.estimatedHours || null,
  dependencies: taskData.dependencies || [],
  attachments: taskData.attachments || [],
  createdBy: data.userId,
  createdAt: data.createdAt,
  action: data.action
};

// Generate task notification message
let message = '';
switch (data.action) {
  case 'create':
    message = `ðŸ“‹ **New Task Created**\n\n`;
    message += `**Title:** ${task.title}\n`;
    message += `**Assignee:** ${task.assignee}\n`;
    message += `**Priority:** ${task.priority}\n`;
    message += `**Project:** ${task.project}\n`;
    if (task.dueDate) message += `**Due Date:** ${new Date(task.dueDate).toLocaleDateString()}\n`;
    if (task.description) message += `**Description:** ${task.description}\n`;
    break;
  case 'update':
    message = `âœï¸ **Task Updated**\n\n`;
    message += `**Title:** ${task.title}\n`;
    message += `**Status:** ${task.status}\n`;
    message += `**Assignee:** ${task.assignee}\n`;
    break;
  case 'delete':
    message = `ðŸ—‘ï¸ **Task Deleted**\n\n`;
    message += `**Title:** ${task.title}\n`;
    message += `**Deleted by:** ${data.userId}\n`;
    break;
  default:
    message = `ðŸ“‹ **Task Action: ${data.action}**\n\n`;
    message += `**Title:** ${task.title}\n`;
}

return {
  json: {
    ...data,
    task,
    notificationMessage: message,
    automationType: 'task'
  }
};

// Process reminder automation
const data = $json;
const reminderData = data.data;

// Create reminder object
const reminder = {
  id: data.id,
  title: reminderData.title || 'Reminder',
  message: reminderData.message || data.description || 'You have a reminder',
  reminderTime: reminderData.reminderTime || data.scheduledTime || new Date().toISOString(),
  recipients: data.recipients,
  frequency: reminderData.frequency || 'once', // once, daily, weekly, monthly
