return {
  json: {
    ...data,
    reminder,
    notificationMessage: message,
    automationType: 'reminder'
  }
};

// Process notification automation
const data = $json;
const notificationData = data.data;

// Create notification object
const notification = {
  id: data.id,
  title: notificationData.title || 'Notification',
  message: notificationData.message || data.description || 'You have a notification',
  type: notificationData.type || 'info', // info, success, warning, error
  recipients: data.recipients,
  channels: notificationData.channels || ['telegram'], // telegram, slack, email
  urgent: data.priority === 'urgent',
  category: data.category,
  tags: data.tags,
  attachments: notificationData.attachments || [],
  actionButtons: notificationData.actionButtons || [],
  createdBy: data.userId,
  createdAt: data.createdAt,
