// Process other automation types (data-sync, report, etc.)
const data = $json;

// Create generic automation object
const automation = {
  id: data.id,
  type: data.type,
  action: data.action,
  data: data.data,
  settings: data.settings,
  recipients: data.recipients,
  category: data.category,
  tags: data.tags,
  priority: data.priority,
