// Generate weekly automation summary
const automations = $input.all();

if (!automations || automations.length === 0) {
  return [{
    json: {
      report: 'No automations were executed in the last 7 days.',
      stats: {
        total: 0,
        byType: {},
        byUser: {},
        byPriority: {}
      }
    }
  }];
}

// Calculate statistics
