const byUser = {};
const byPriority = {};
const byAction = {};
const byDay = {};

automations.forEach(item => {
  const auto = item.json;

  // By type
  byType[auto.type] = (byType[auto.type] || 0) + 1;

  // By user
  byUser[auto.userId] = (byUser[auto.userId] || 0) + 1;

  // By priority
  byPriority[auto.priority] = (byPriority[auto.priority] || 0) + 1;

  // By action
  byAction[auto.action] = (byAction[auto.action] || 0) + 1;

  // By day
  const day = new Date(auto.executedAt).toDateString();
  byDay[day] = (byDay[day] || 0) + 1;
});

// Generate report
let report = `ðŸ¤– **Weekly Automation Hub Summary**\n`;
report += `ðŸ“… **Period:** Last 7 days\n`;
report += `â° **Generated:** ${new Date().toISOString()}\n\n`;

