// Define APIs to monitor
const apisToMonitor = [
  {
    name: 'Main API',
    url: 'https://api.yourapp.com/health',
    method: 'GET',
    expectedStatus: 200,
    timeout: 10000,
    headers: {
      'Authorization': 'Bearer YOUR_API_TOKEN',
      'Content-Type': 'application/json'
    },
    expectedResponse: {
      status: 'healthy'
    },
    critical: true
  },
  {
    name: 'User API',
    url: 'https://api.yourapp.com/users/me',
    method: 'GET',
    expectedStatus: 200,
    timeout: 10000,
    headers: {
      'Authorization': 'Bearer YOUR_API_TOKEN'
    },
    critical: true
  },
  {
    name: 'Payment API',
    url: 'https://api.yourapp.com/payments/status',
    method: 'GET',
    expectedStatus: 200,
    timeout: 15000,
    headers: {
      'Authorization': 'Bearer YOUR_API_TOKEN'
    },
    critical: true
  },
  {
    name: 'Analytics API',
    url: 'https://api.yourapp.com/analytics/ping',
    method: 'GET',
    expectedStatus: 200,
    timeout: 10000,
    headers: {
      'Authorization': 'Bearer YOUR_API_TOKEN'
    },
    critical: false
  },
  {
    name: 'External Service',
    url: 'https://external-api.service.com/status',
    method: 'GET',
    expectedStatus: 200,
    timeout: 20000,
    critical: false
  }
];

return apisToMonitor.map(api => ({
  json: {
    ...api,
    checkId: `${api.name.toLowerCase().replace(/\s+/g, '-')}-${Date.now()}`,
    timestamp: new Date().toISOString()
  }
}));

// Process API health check results
const apiConfig = $json;
const response = $json.response;
const startTime = new Date(apiConfig.timestamp);
const endTime = new Date();
const responseTime = endTime.getTime() - startTime.getTime();

// Determine health status
let status = 'healthy';
let issues = [];
let severity = 'info';

// Check if request failed
if (!response || response.error) {
  status = 'down';
  issues.push('API request failed');
  severity = apiConfig.critical ? 'critical' : 'warning';
}

// Check status code
else if (response.statusCode !== apiConfig.expectedStatus) {
  status = 'unhealthy';
  issues.push(`Unexpected status code: ${response.statusCode} (expected: ${apiConfig.expectedStatus})`);
  severity = response.statusCode >= 500 ? 'critical' : 'warning';
}

// Check response time
else if (responseTime > (apiConfig.timeout * 0.8)) {
  status = 'slow';
  issues.push(`Slow response time: ${responseTime}ms (timeout: ${apiConfig.timeout}ms)`);
  severity = 'warning';
}

// Check expected response content
else if (apiConfig.expectedResponse && response.body) {
  try {
    const responseBody = typeof response.body === 'string' ? JSON.parse(response.body) : response.body;

    for (const [key, expectedValue] of Object.entries(apiConfig.expectedResponse)) {
      if (responseBody[key] !== expectedValue) {
        status = 'unhealthy';
        issues.push(`Unexpected response content: ${key} = ${responseBody[key]} (expected: ${expectedValue})`);
        severity = 'warning';
        break;
      }
    }
  } catch (error) {
    status = 'unhealthy';
    issues.push('Invalid JSON response');
    severity = 'warning';
  }
}

// Determine overall health
if (status === 'healthy' && responseTime < (apiConfig.timeout * 0.5)) {
  severity = 'info';
} else if (status === 'healthy' && responseTime < (apiConfig.timeout * 0.8)) {
  severity = 'info';
}

const result = {
  checkId: apiConfig.checkId,
  apiName: apiConfig.name,
  url: apiConfig.url,
  method: apiConfig.method,
  status,
  severity,
  responseTime,
  statusCode: response?.statusCode || null,
  issues,
  critical: apiConfig.critical,
  timestamp: apiConfig.timestamp,
  checkedAt: endTime.toISOString(),
  responseBody: response?.body ? (typeof response.body === 'string' ? response.body.substring(0, 500) : JSON.stringify(response.body).substring(0, 500)) : null,
  error: response?.error || null
};

return { json: result };

