// Generate daily API monitoring report
const data = $input.all();

if (!data || data.length === 0) {
  return [{
    json: {
      report: 'No monitoring data available for the last 24 hours.',
      summary: {
        totalChecks: 0,
        healthyChecks: 0,
        unhealthyChecks: 0,
        downChecks: 0,
        averageResponseTime: 0
      }
    }
  }];
}

// Group data by API
const apiGroups = {};
data.forEach(item => {
  const apiName = item.json.apiName;
  if (!apiGroups[apiName]) {
    apiGroups[apiName] = [];
  }
  apiGroups[apiName].push(item.json);
});

// Calculate statistics
let totalChecks = data.length;
let healthyChecks = data.filter(item => item.json.status === 'healthy').length;
let unhealthyChecks = data.filter(item => item.json.status === 'unhealthy' || item.json.status === 'slow').length;
let downChecks = data.filter(item => item.json.status === 'down').length;
let criticalIssues = data.filter(item => item.json.severity === 'critical').length;

// Calculate average response time
const responseTimes = data.filter(item => item.json.responseTime).map(item => item.json.responseTime);
const averageResponseTime = responseTimes.length > 0 ? Math.round(responseTimes.reduce((a, b) => a + b, 0) / responseTimes.length) : 0;

// Calculate uptime percentage
const uptimePercentage = totalChecks > 0 ? Math.round((healthyChecks / totalChecks) * 100) : 0;

// Generate API-specific reports
const apiReports = Object.entries(apiGroups).map(([apiName, checks]) => {
  const apiHealthy = checks.filter(check => check.status === 'healthy').length;
  const apiTotal = checks.length;
  const apiUptime = apiTotal > 0 ? Math.round((apiHealthy / apiTotal) * 100) : 0;
  const apiAvgResponseTime = checks.filter(check => check.responseTime).length > 0 
    ? Math.round(checks.filter(check => check.responseTime).reduce((a, b) => a + b.responseTime, 0) / checks.filter(check => check.responseTime).length)
    : 0;

  const recentIssues = checks.filter(check => check.status !== 'healthy').slice(-3);

  return {
    name: apiName,
    uptime: apiUptime,
    totalChecks: apiTotal,
    healthyChecks: apiHealthy,
    averageResponseTime: apiAvgResponseTime,
    recentIssues: recentIssues.map(issue => ({
      status: issue.status,
      issues: issue.issues,
      time: issue.checkedAt
    }))
  };
});

// Generate report text
let report = `ðŸ“Š **Daily API Monitoring Report**\n`;
report += `ðŸ“… **Period:** Last 24 hours\n`;
report += `â° **Generated:** ${new Date().toISOString()}\n\n`;

report += `ðŸ“ˆ **Overall Statistics:**\n`;
report += `â€¢ Total Checks: ${totalChecks}\n`;
report += `â€¢ Healthy: ${healthyChecks} (${uptimePercentage}%)\n`;
report += `â€¢ Issues: ${unhealthyChecks + downChecks}\n`;
report += `â€¢ Critical Issues: ${criticalIssues}\n`;
report += `â€¢ Average Response Time: ${averageResponseTime}ms\n\n`;

report += `ðŸ” **API Details:**\n`;
apiReports.forEach(api => {
  const statusEmoji = api.uptime >= 99 ? 'âœ…' : api.uptime >= 95 ? 'âš ï¸' : 'ðŸš¨';
  report += `\n${statusEmoji} **${api.name}**\n`;
  report += `  â€¢ Uptime: ${api.uptime}% (${api.healthyChecks}/${api.totalChecks})\n`;
  report += `  â€¢ Avg Response: ${api.averageResponseTime}ms\n`;

  if (api.recentIssues.length > 0) {
    report += `  â€¢ Recent Issues:\n`;
    api.recentIssues.forEach(issue => {
      report += `    - ${issue.status}: ${issue.issues.join(', ')}\n`;
    });
  }
});

report += `\n[ðŸ“Š View Full Dashboard](https://docs.google.com/spreadsheets/d/your-api-monitoring-sheet-id)`;

const summary = {
  totalChecks,
  healthyChecks,
  unhealthyChecks,
  downChecks,
  criticalIssues,
  averageResponseTime,
  uptimePercentage,
  apiCount: Object.keys(apiGroups).length,
  apiReports
};

return [{
  json: {
    report,
    summary,
    generatedAt: new Date().toISOString()
  }
}];
