// Parse and validate incoming content
const payload = $json.body;

// Extract content data
const contentData = {
  id: payload.id || `content_${Date.now()}`,
  type: payload.type || 'text', // text, image, video, audio
  content: payload.content || '',
  url: payload.url || null,
  userId: payload.userId || 'anonymous',
  username: payload.username || 'Unknown User',
  platform: payload.platform || 'unknown', // discord, telegram, website, etc.
  channelId: payload.channelId || null,
  channelName: payload.channelName || 'Unknown Channel',
  timestamp: payload.timestamp || new Date().toISOString(),
  metadata: payload.metadata || {}
};

// Validate required fields
const isValid = contentData.content || contentData.url;

// Determine moderation strategy based on content type
const moderationStrategy = {
  needsTextAnalysis: contentData.type === 'text' && contentData.content,
  needsImageAnalysis: contentData.type === 'image' && contentData.url,
  needsVideoAnalysis: contentData.type === 'video' && contentData.url,
  needsAudioAnalysis: contentData.type === 'audio' && contentData.url
};

return {
  json: {
    ...contentData,
    isValid,
    moderationStrategy,
    processedAt: new Date().toISOString(),
    moderationId: `mod_${contentData.id}_${Date.now()}`
  }
};

