// Combine and process moderation results
const contentData = $('Parse Content').item.json;
const openaiModeration = $('OpenAI Moderation').item?.json;
const advancedTextAnalysis = $('Advanced Text Analysis').item?.json;
const imageAnalysis = $('Image Analysis').item?.json;

// Initialize moderation result
let moderationResult = {
  contentId: contentData.id,
  moderationId: contentData.moderationId,
  flagged: false,
  categories: [],
  severity: 'low',
  confidence: 0,
  reason: '',
  action: 'approve',
  sources: []
};

// Process OpenAI moderation results
if (openaiModeration && openaiModeration.results && openaiModeration.results.length > 0) {
  const result = openaiModeration.results[0];
  if (result.flagged) {
    moderationResult.flagged = true;
    moderationResult.sources.push('OpenAI Moderation');

    // Extract flagged categories
    Object.keys(result.categories).forEach(category => {
      if (result.categories[category]) {
        moderationResult.categories.push(category);
      }
    });

    // Determine severity based on category scores
    const maxScore = Math.max(...Object.values(result.category_scores));
    if (maxScore > 0.8) {
      moderationResult.severity = 'high';
    } else if (maxScore > 0.5) {
      moderationResult.severity = 'medium';
    }

    moderationResult.confidence = maxScore;
  }
}

// Process advanced text analysis
if (advancedTextAnalysis && advancedTextAnalysis.choices && advancedTextAnalysis.choices.length > 0) {
  try {
    const analysisContent = advancedTextAnalysis.choices[0].message.content;
    const analysis = JSON.parse(analysisContent);

    if (analysis.flagged) {
      moderationResult.flagged = true;
      moderationResult.sources.push('Advanced Text Analysis');
      moderationResult.categories = [...new Set([...moderationResult.categories, ...analysis.categories])];

      // Use higher severity
      const severityLevels = { low: 1, medium: 2, high: 3 };
      if (severityLevels[analysis.severity] > severityLevels[moderationResult.severity]) {
        moderationResult.severity = analysis.severity;
      }

      // Use higher confidence
      if (analysis.confidence > moderationResult.confidence) {
        moderationResult.confidence = analysis.confidence;
      }

      moderationResult.reason += (moderationResult.reason ? ' | ' : '') + analysis.reason;

      // Use most restrictive action
      const actionLevels = { approve: 1, review: 2, block: 3 };
      if (actionLevels[analysis.action] > actionLevels[moderationResult.action]) {
        moderationResult.action = analysis.action;
      }
    }
  } catch (error) {
    console.log('Error parsing advanced text analysis:', error);
  }
}

// Process image analysis
if (imageAnalysis && imageAnalysis.choices && imageAnalysis.choices.length > 0) {
  try {
    const analysisContent = imageAnalysis.choices[0].message.content;
    const analysis = JSON.parse(analysisContent);

    if (analysis.flagged) {
      moderationResult.flagged = true;
      moderationResult.sources.push('Image Analysis');
      moderationResult.categories = [...new Set([...moderationResult.categories, ...analysis.categories])];

      // Use higher severity
      const severityLevels = { low: 1, medium: 2, high: 3 };
      if (severityLevels[analysis.severity] > severityLevels[moderationResult.severity]) {
        moderationResult.severity = analysis.severity;
      }

      // Use higher confidence
      if (analysis.confidence > moderationResult.confidence) {
        moderationResult.confidence = analysis.confidence;
      }

      moderationResult.reason += (moderationResult.reason ? ' | ' : '') + analysis.reason;

      // Use most restrictive action
      const actionLevels = { approve: 1, review: 2, block: 3 };
      if (actionLevels[analysis.action] > actionLevels[moderationResult.action]) {
        moderationResult.action = analysis.action;
      }
    }
  } catch (error) {
    console.log('Error parsing image analysis:', error);
  }
}

// Final decision logic
if (!moderationResult.flagged) {
  moderationResult.action = 'approve';
  moderationResult.reason = 'Content passed all moderation checks';
}

return {
  json: {
    ...contentData,
    moderation: moderationResult,
    needsAction: moderationResult.action !== 'approve',
    timestamp: new Date().toISOString()
  }
};

