// Define websites and backup sources to monitor
const websites = [
  {
    name: 'Main Website',
    url: 'https://yourwebsite.com',
    backupUrl: 'https://yourwebsite.com/backup-status',
    expectedFiles: ['database.sql', 'files.tar.gz'],
    maxAge: 24 // hours
  },
  {
    name: 'Blog',
    url: 'https://blog.yourwebsite.com',
    backupUrl: 'https://blog.yourwebsite.com/wp-admin/backup-status.php',
    expectedFiles: ['wp-database.sql', 'wp-content.zip'],
    maxAge: 24
  },
  {
    name: 'E-commerce Store',
    url: 'https://store.yourwebsite.com',
    backupUrl: 'https://store.yourwebsite.com/admin/backup-check',
    expectedFiles: ['store-db.sql', 'product-images.zip'],
    maxAge: 12
  }
];

return websites.map(site => ({ json: site }));

// Analyze website and backup status
const websiteData = $('Define Websites').item.json;
const websiteResponse = $('Check Website Status').item;
const backupResponse = $('Check Backup Status').item;

const currentTime = new Date();
const checkId = `${websiteData.name.replace(/\s+/g, '_')}_${currentTime.getTime()}`;

// Check website status
const websiteStatus = {
  isUp: websiteResponse.json && websiteResponse.statusCode >= 200 && websiteResponse.statusCode < 400,
  statusCode: websiteResponse.statusCode || 0,
  responseTime: websiteResponse.responseTime || 0,
  error: websiteResponse.error || null
};

// Check backup status
let backupStatus = {
  isHealthy: false,
  lastBackup: null,
  missingFiles: [],
  error: null,
  age: null
};

if (backupResponse.json && backupResponse.statusCode >= 200 && backupResponse.statusCode < 400) {
  try {
    const backupData = typeof backupResponse.json === 'string' ? 
      JSON.parse(backupResponse.json) : backupResponse.json;

    // Parse backup information
    const lastBackupTime = new Date(backupData.lastBackup || backupData.last_backup || backupData.timestamp);
    const ageInHours = (currentTime - lastBackupTime) / (1000 * 60 * 60);

    backupStatus = {
      isHealthy: ageInHours <= websiteData.maxAge,
      lastBackup: lastBackupTime.toISOString(),
      age: ageInHours,
      files: backupData.files || [],
      missingFiles: websiteData.expectedFiles.filter(file => 
        !(backupData.files || []).includes(file)
      ),
      error: null
    };
  } catch (error) {
    backupStatus.error = `Failed to parse backup data: ${error.message}`;
  }
} else {
  backupStatus.error = `Backup endpoint error: ${backupResponse.statusCode} - ${backupResponse.error}`;
}

// Determine overall health
const overallHealth = websiteStatus.isUp && backupStatus.isHealthy && backupStatus.missingFiles.length === 0;

// Generate alerts
const alerts = [];
if (!websiteStatus.isUp) {
  alerts.push(`ðŸ”´ Website is DOWN (Status: ${websiteStatus.statusCode})`);
}
if (!backupStatus.isHealthy) {
  if (backupStatus.age !== null) {
    alerts.push(`âš ï¸ Backup is ${backupStatus.age.toFixed(1)} hours old (Max: ${websiteData.maxAge}h)`);
  } else {
    alerts.push(`âŒ Backup status unknown`);
  }
}
if (backupStatus.missingFiles.length > 0) {
  alerts.push(`ðŸ“ Missing backup files: ${backupStatus.missingFiles.join(', ')}`);
}
if (backupStatus.error) {
  alerts.push(`ðŸš¨ Backup check error: ${backupStatus.error}`);
}

const result = {
  checkId: checkId,
  timestamp: currentTime.toISOString(),
  website: {
    name: websiteData.name,
    url: websiteData.url,
    ...websiteStatus
  },
  backup: backupStatus,
  overallHealth: overallHealth,
  alerts: alerts,
  needsAttention: alerts.length > 0
};

return { json: result };

