// Generate weekly backup monitoring report
const logs = $input.all();

if (logs.length === 0) {
  return { json: { noData: true, message: 'No monitoring data for the past week' } };
}

// Group by website
const websiteStats = {};
let totalChecks = 0;
let totalIssues = 0;

logs.forEach(log => {
  const data = log.json;
  const website = data.website;

  if (!websiteStats[website]) {
    websiteStats[website] = {
      totalChecks: 0,
      healthyChecks: 0,
      downtime: 0,
      backupIssues: 0,
      avgResponseTime: 0,
      responseTimeSum: 0,
      lastCheck: null
    };
  }

  const stats = websiteStats[website];
  stats.totalChecks++;
  totalChecks++;

  if (data.overallHealth === 'HEALTHY') {
    stats.healthyChecks++;
  } else {
    totalIssues++;
  }

  if (data.websiteStatus === 'DOWN') {
    stats.downtime++;
  }

  if (data.backupHealthy === 'UNHEALTHY') {
    stats.backupIssues++;
  }

  const responseTime = parseFloat(data.websiteResponseTime) || 0;
  stats.responseTimeSum += responseTime;
  stats.avgResponseTime = stats.responseTimeSum / stats.totalChecks;

  const checkTime = new Date(data.timestamp);
  if (!stats.lastCheck || checkTime > new Date(stats.lastCheck)) {
    stats.lastCheck = data.timestamp;
  }
});

// Calculate uptime percentages
Object.keys(websiteStats).forEach(website => {
  const stats = websiteStats[website];
  stats.uptimePercentage = ((stats.totalChecks - stats.downtime) / stats.totalChecks * 100).toFixed(2);
  stats.backupHealthPercentage = ((stats.totalChecks - stats.backupIssues) / stats.totalChecks * 100).toFixed(2);
});

// Generate report
const weekStart = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toLocaleDateString();
const weekEnd = new Date().toLocaleDateString();

const report = `ðŸ“Š **Weekly Backup Monitoring Report** (${weekStart} - ${weekEnd})

` +
  `**Overview:**
` +
  `â€¢ Total Checks: ${totalChecks}
` +
  `â€¢ Issues Found: ${totalIssues}
` +
  `â€¢ Success Rate: ${((totalChecks - totalIssues) / totalChecks * 100).toFixed(1)}%

` +
  `**Website Details:**
` +
  Object.entries(websiteStats).map(([website, stats]) => 
    `**${website}:**
` +
    `â€¢ Uptime: ${stats.uptimePercentage}% (${stats.totalChecks - stats.downtime}/${stats.totalChecks})
` +
    `â€¢ Backup Health: ${stats.backupHealthPercentage}% (${stats.totalChecks - stats.backupIssues}/${stats.totalChecks})
` +
    `â€¢ Avg Response Time: ${stats.avgResponseTime.toFixed(0)}ms
` +
    `â€¢ Last Check: ${new Date(stats.lastCheck).toLocaleString()}
`
  ).join('\n') +
  `
[ðŸ“ˆ View Full Dashboard](https://docs.google.com/spreadsheets/d/your-backup-monitoring-sheet-id)`;

return {
  json: {
    report: report,
    totalChecks: totalChecks,
    totalIssues: totalIssues,
    successRate: ((totalChecks - totalIssues) / totalChecks * 100).toFixed(1),
    websiteStats: websiteStats,
    weekStart: weekStart,
    weekEnd: weekEnd
  }
};
